mapper.select:
SELECT EXCE_ID, EXCE_TYPE, LEVEL, MODEL, BUS_TYPE, EX_CODE, EX_DESC, CURRENT_STATE, DESCRIPTION, DATE_FORMAT(CREATEDATE,'%Y-%m-%d %H:%i:%s') as createDate, DATE_FORMAT(UPDATEDATE,'%Y-%m-%d %H:%i:%s') as updateDate, UPDATEBY, ORDER_ID FROM BMS_EXCEPTION WHERE 1=1
AND ORDER_ID LIKE CONCAT('%',#{orderId},'%')
AND MODEL = #{model}
AND BUS_TYPE = #{busType}
AND CURRENT_STATE = #{currentState}
AND DATE_FORMAT(CREATEDATE ,'%Y-%m-%d') >= #{beginCreateTime}
AND DATE_FORMAT(CREATEDATE ,'%Y-%m-%d') <= #{endCreateTime}
AND DATE_FORMAT(UPDATEDATE ,'%Y-%m-%d') >= #{beginUpdateTime}
AND DATE_FORMAT(UPDATEDATE ,'%Y-%m-%d') <= #{endUpdateTime}
ORDER BY CREATEDATE DESC
