mapper.select:
SELECT T.BAL_DATE, CONCAT(B.SEND_DATE,'',B.SEND_TIME) AS sendTime, CONCAT(B.RTN_DATE,'',B.RTN_TIME) AS resultTime, IFNULL((SELECT TYPE_NAME FROM BASE_TYPE WHERE TYPE_SORT = 'CHANNEL_ID' AND TYPE_CODE = T.CHANNEL_ID),T.CHANNEL_ID) CHANNEL_ID, IFNULL((SELECT TYPE_NAME FROM BASE_TYPE WHERE TYPE_SORT = 'BUSINESS_TYPE' AND TYPE_CODE = B.BUSINESS_TYPE),B.BUSINESS_TYPE) TRANS_TYPE, B.WORK_DATE, B.CLEAR_ID, A.PLATFORM_ID, B.TRANS_AMT, A.TRANS_AMT AS transAmtC, B.BAL_MEMO FROM BAL_FILE_BATCH T INNER JOIN BAL_JGKJ_SEVEN_RESULT_EXCEPTION B ON B.BATCH_ID = T.BATCH_ID AND T.STATUS='BAL_OVER' AND B.BAL_RESULT IN('BAL_ERROR') LEFT JOIN BAL_JGKJ_RESULT_EXCEPTION A ON A.BATCH_ID = T.BATCH_ID AND T.STATUS='BAL_OVER' AND A.BAL_RESULT IN('BAL_ERROR') AND B.CLEAR_ID = A.SEVENPAY_ID
AND T.BAL_DATE = #{balDate,jdbcType = VARCHAR}
AND B.CLEAR_ID LIKE CONCAT('%',#{clearId},'%')
AND B.WORK_DATE >= #{beginBalTime, jdbcType=VARCHAR}
AND #{endBalTime, jdbcType=VARCHAR} >= B.WORK_DATE
ORDER BY A.INST_DATE DESC
