mapper.select:
SELECT STT.WITHDRAW_SN, STT.ID AS TRADE_ID, STT.MOBILE, STT.CUST_ID, STT.CUST_NAME, STT.WITHDRAW_ACCT_TYPE, STT.BNAK_ACCT_NO_INTERNAL, STT.CARD_NO, STT.BANK_NAME, STT.BANK_ACCT_NAME, STT.BANK_BRANCH_NAME, STT.WITHDRAW_AMT, STT.WITHDRAW_TYPE, STT.WITHDRAW_STATE, STT.FEE, STT.FEE_CUST_ID, STT.FEE_STATE, STT.CORE_SN, STT.CORE_RETURN_CODE, STT.CORE_RETURN_MSG, DATE_FORMAT(STT.SUBMIT_TIME,'%Y-%m-%d %H:%i:%s') AS SUBMIT_TIME, STT.RESULT_RETURN_TIME, STT.CREATE_ID, DATE_FORMAT(STT.CREATE_TIME,'%Y-%m-%d %H:%i:%s') AS CREATE_TIME, (SELECT U.USER_NAME FROM SYS_USER U WHERE U.USER_ID = STT.MODIFY_ID) MODIFY_ID, DATE_FORMAT(STT.AUDIT_TIME,'%Y-%m-%d %H:%i:%s') AS AUDIT_TIME, STT.AUDIT_STATE, DATE_FORMAT(STT.VERIFICATION_DATE,'%Y-%m-%d %H:%i:%s') AS VERIFICATION_DATE, STT.VERIFICATION_STATE, (SELECT U.USER_NAME FROM SYS_USER U WHERE U.USER_ID = STT.VERIFICATION_USER) VERIFICATION_USER, CB.WITHDRAW_REQSERIALID, K.CLEAR_ID FROM (SELECT T.*, Y.ID, L.MOBILE FROM TD_WITHDRAW_BILL T JOIN TD_LOGIN_USER_INFO L ON T.CUST_ID = L.CUST_ID JOIN STCS.TRANS_RECORD_FLOW F ON T.CORE_SN=F.MSG_ID AND F.OPERATE='JGKJ_PAYMENT' LEFT JOIN STCS.CLEAR_JGKJ_TRADE Y ON F.ID=Y.TRANS_FLOW_ID
) STT LEFT JOIN TD_WITHDRAW_CHILD_BILL CB ON STT.WITHDRAW_SN = CB.WITHDRAW_SN LEFT JOIN BMS_CLEAR_KINGDEE_PAY K ON CB.WITHDRAW_REQSERIALID =K.OPER_ID AND CB.OPER_TYPE = 'withdraw' WHERE 1=1
AND STT.WITHDRAW_SN LIKE CONCAT('%',#{withdrawSn,jdbcType=VARCHAR},'%')
AND STT.CARD_NO LIKE CONCAT('%',#{cardNo,jdbcType=VARCHAR},'%')
AND STT.ID LIKE CONCAT('%',#{tradeId,jdbcType=VARCHAR},'%')
AND STT.MOBILE LIKE CONCAT('%',#{mobile,jdbcType=VARCHAR},'%')
AND STT.VERIFICATION_STATE = #{verificationState,jdbcType=VARCHAR}
AND STT.AUDIT_STATE = #{auditState,jdbcType=VARCHAR}
AND DATE_FORMAT(STT.CREATE_TIME ,'%Y-%m-%d') >= #{beginTime,jdbcType=VARCHAR}
AND DATE_FORMAT(STT.CREATE_TIME ,'%Y-%m-%d') <= #{endTime,jdbcType=VARCHAR}
AND DATE_FORMAT(STT.AUDIT_TIME ,'%Y-%m-%d') >= #{auditBeginTime,jdbcType=VARCHAR}
AND DATE_FORMAT(STT.AUDIT_TIME ,'%Y-%m-%d') <= #{auditEndTime,jdbcType=VARCHAR}
AND STT.WITHDRAW_STATE = #{withdrawState,jdbcType=VARCHAR}
AND K.CLEAR_ID = #{clearId,jdbcType=VARCHAR}
AND CB.WITHDRAW_REQSERIALID = #{withdrawReqserialid,jdbcType=VARCHAR}
ORDER BY STT.CREATE_TIME DESC

update.foreach:
UPDATE TD_WITHDRAW_BILL T
T.VERIFICATION_STATE = '02', T.VERIFICATION_DATE = now()
where T.WITHDRAW_SN = ${item}

mapper.select:
SELECT STT.WITHDRAW_SN, STT.ID AS TRADE_ID, STT.MOBILE, STT.CUST_NAME, STT.BANK_ACCT_NAME, STT.CARD_NO, STT.BANK_NAME, STT.BANK_BRANCH_NAME, STT.WITHDRAW_AMT, STT.FEE, DATE_FORMAT(STT.CREATE_TIME,'%Y-%m-%d %H:%i:%s') AS CREATE_TIME, CASE STT.WITHDRAW_STATE WHEN '00' THEN '提现成功' WHEN '01' THEN '待提现' WHEN '02' THEN '提现申请提交核心' WHEN '03' THEN '提现申请成功' WHEN '04' THEN '提现申请失败' WHEN '22' THEN '提现提交核心' WHEN '23' THEN '提现核心处理成功' WHEN '24' THEN '提现核心处理失败' WHEN '32' THEN '提现提交金蝶' WHEN '33' THEN '提现金蝶处理成功' WHEN '34' THEN '提现金蝶处理失败' WHEN '42' THEN '提现撤销提交核心' WHEN '43' THEN '提现撤销成功' WHEN '44' THEN '提现撤销核心处理失败' END AS WITHDRAW_STATE, CASE STT.AUDIT_STATE WHEN '01' THEN '待审核' WHEN '02' THEN '审核通过' WHEN '04' THEN '审核不通过' END AS AUDIT_STATE, (SELECT U.USER_NAME FROM SYS_USER U WHERE U.USER_ID = STT.MODIFY_ID) MODIFY_ID, DATE_FORMAT(STT.AUDIT_TIME,'%Y-%m-%d %H:%i:%s') AS AUDIT_TIME, CASE STT.VERIFICATION_STATE WHEN '01' THEN '未核销' WHEN '02' THEN '已核销' END AS VERIFICATION_STATE, (SELECT U.USER_NAME FROM SYS_USER U WHERE U.USER_ID = STT.VERIFICATION_USER) VERIFICATION_USER, DATE_FORMAT(STT.VERIFICATION_DATE,'%Y-%m-%d %H:%i:%s') AS VERIFICATION_DATE, CB.WITHDRAW_REQSERIALID, K.CLEAR_ID FROM (SELECT T.*, Y.ID, L.MOBILE FROM TD_WITHDRAW_BILL T JOIN TD_LOGIN_USER_INFO L ON T.CUST_ID = L.CUST_ID JOIN STCS.TRANS_RECORD_FLOW F ON T.CORE_SN=F.MSG_ID AND F.OPERATE='JGKJ_PAYMENT' left JOIN STCS.CLEAR_JGKJ_TRADE Y ON F.ID=Y.TRANS_FLOW_ID ) STT LEFT JOIN TD_WITHDRAW_CHILD_BILL CB ON STT.WITHDRAW_SN = CB.WITHDRAW_SN LEFT JOIN BMS_CLEAR_KINGDEE_PAY K ON CB.WITHDRAW_REQSERIALID =K.OPER_ID AND CB.OPER_TYPE = 'withdraw' WHERE 1=1
AND STT.WITHDRAW_SN LIKE CONCAT('%',#{withdrawSn,jdbcType=VARCHAR},'%')
AND STT.CARD_NO LIKE CONCAT('%',#{cardNo,jdbcType=VARCHAR},'%')
AND STT.ID LIKE CONCAT('%',#{tradeId,jdbcType=VARCHAR},'%')
AND STT.MOBILE LIKE CONCAT('%',#{mobile,jdbcType=VARCHAR},'%')
AND STT.VERIFICATION_STATE = #{verificationState,jdbcType=VARCHAR}
AND STT.AUDIT_STATE = #{auditState,jdbcType=VARCHAR}
AND DATE_FORMAT(STT.CREATE_TIME ,'%Y-%m-%d') >= #{beginTime,jdbcType=VARCHAR}
AND DATE_FORMAT(STT.CREATE_TIME ,'%Y-%m-%d') <= #{endTime,jdbcType=VARCHAR}
AND DATE_FORMAT(STT.AUDIT_TIME ,'%Y-%m-%d') >= #{auditBeginTime,jdbcType=VARCHAR}
AND DATE_FORMAT(STT.AUDIT_TIME ,'%Y-%m-%d') <= #{auditEndTime,jdbcType=VARCHAR}
AND STT.WITHDRAW_STATE = #{withdrawState,jdbcType=VARCHAR}
AND K.CLEAR_ID = #{clearId,jdbcType=VARCHAR}
AND CB.WITHDRAW_REQSERIALID = #{withdrawReqserialid,jdbcType=VARCHAR}
ORDER BY STT.CREATE_TIME DESC
