mapper.select:
SELECT A.ACCT_ID, T.CUST_NAME ACCT_NAME, A.CUST_ID, U.MOBILE, A.BALANCE, A.BALANCE_FLAG, A.FREEZE_AMT, A.SUBJECT_ID, A.ONWAY_AMT, A.USABLE_AMT, A.STATUS, A.INST_DATE, DATE_FORMAT(A.INST_DATETIME,'%Y-%m-%d %H:%i:%s') AS INST_DATETIME, A.JGKJ_CARD_NO FROM STCS.ACCT_SEVEN_CUST A JOIN TD_LOGIN_USER_INFO U ON A.CUST_ID = U.CUST_ID JOIN TD_CUST_INFO T ON A.CUST_ID = T.CUST_ID WHERE 1 = 1
AND U.MOBILE =#{mobile,jdbcType=VARCHAR}
AND A.ACCT_NAME LIKE CONCAT('%',#{acctName},'%')
AND A.JGKJ_CARD_NO LIKE CONCAT('%',#{jgkjCardNo},'%')
AND A.ACCT_ID LIKE CONCAT('%',#{acctId},'%')
AND A.STATUS = #{status,jdbcType = VARCHAR}
AND DATE_FORMAT(A.INST_DATETIME ,'%Y-%m-%d') >= #{createBeginTime}
AND DATE_FORMAT(A.INST_DATETIME ,'%Y-%m-%d') <= #{createEndTime}
ORDER BY A.INST_DATETIME DESC
