mapper.select:
SELECT distinct C.*, DATE_FORMAT(C.CREATE_TIME,'%Y-%m-%d %H:%i:%s') createTime, U.EMAIL AS EMAIL, U.MOBILE AS MOBILE, U.STATE merchantState, C.STATE auditState, A.AUDIT_INFO aduitMessage, S.USER_NAME aduitUserName, M.USER_NAME custManagerName FROM TD_CUST_INFO C INNER JOIN TD_LOGIN_USER_INFO U ON C.CUST_ID = U.CUST_ID LEFT JOIN TD_AUDIT_RECODE_INFO A ON C.MERCHANT_CODE = A.MERCHANT_CODE LEFT JOIN SYS_USER S ON A.USER_ID = S.USER_ID LEFT JOIN SYS_USER M ON C.CUSTMANAGER IS NOT NULL AND C.CUSTMANAGER = M.USER_ID
C.MERCHANT_FLAG = '3' AND C.STATE !="" AND C.MERCHANT_CODE !=""
AND DATE_FORMAT(C.CREATE_TIME ,'%Y-%m-%d')>= #{startCreateTime}
AND DATE_FORMAT(C.CREATE_TIME ,'%Y-%m-%d')<= #{endCreateTime}
AND C.STATE = #{auditState, jdbcType=VARCHAR}
AND U.STATE = #{merchantState, jdbcType=VARCHAR}
AND C.CUST_NAME LIKE CONCAT('%',#{custName},'%')
AND U.EMAIL LIKE CONCAT('%',#{email},'%')
AND U.MOBILE LIKE CONCAT('%',#{mobile},'%')
AND C.MERCHANT_CODE LIKE CONCAT('%',#{merchantCode},'%')
AND C.CUSTMANAGER = M.USER_ID AND M.USER_NAME LIKE CONCAT('%',#{custManager},'%')
ORDER BY C.CREATE_TIME DESC

SELECT C.MERCHANT_CODE, C.CUST_NAME, C.TRADE_PWD, C.ATTACH_STR, U.WRONG_PWD_COUNT, C.CERTIFY_TYPE, C.CERTIFY_NO, C.CUST_TYPE, U.STATE merchantState, U.EMAIL, C.MERCHANT_FLAG, C.CUST_SCORE, C.CUST_LVL, C.TRUST_CERTIFY_LVL, C.TRUST_CERTIFY_AUDIT_STATE, C.CUST_INFO_LVL, C.CUST_ADD, C.CUST_POST_CODE, C.BUSINESS_LICENSE, C.TAX_REG_CERT, C.REPRESENTATIVE_CERT_TYPE, C.REPRESENTATIVE_CERT_NO, C.REPRESENTATIVE_NAME, C.REG_CAPITAL, C.ENT_TYPE, C.INDUSTRY_BELONG, C.YEAR_TURNOVER, C.MERCHANT_WEB_SITE, C.IS_USE_CERTIFICATION, C.IS_USE_MSG, C.QFQ_ACC_ID, C.QFB_ACC_ID, C.CREATE_ID, C.CREATE_TIME, C.MODIFY_ID, C.MODIFY_TIME, C.BUSINESS_TERM, C.BUSINESS_REG_ADDR, C.CONTACT_PHONE, C.FCONTACTUNIT_NUMBER, C.ORG_INST_CODE, C.REPRESENTATIVE_ADDR, C.REPRESENTATIVE_MOBILE, C.AGENT_NAME, C.AGENT_CERT_TYPE, C.AGENT_CERT_CODE, C.AGENT_MOBILE, C.COMP_MAIN_ACCT, C.COMP_ACCT_BANK, C.PWD_FREEZE_TIME, C.COMP_VERIFY_AMT, C.BRANCH_BANK, C.BANK_ACCT_NAME, C.COMMENT, C.STATE auditState FROM TD_CUST_INFO C INNER JOIN TD_LOGIN_USER_INFO U ON C.CUST_ID = U.CUST_ID LEFT JOIN TD_AUDIT_RECODE_INFO A ON C.MERCHANT_CODE = A.MERCHANT_CODE LEFT JOIN SYS_USER S ON A.USER_ID = S.USER_ID
C.MERCHANT_FLAG = '3' AND C.STATE !="" AND C.MERCHANT_CODE !=""
AND DATE_FORMAT(C.CREATE_TIME ,'%Y-%m-%d')>= #{startCreateTime}
AND DATE_FORMAT(C.CREATE_TIME ,'%Y-%m-%d')<= #{endCreateTime}
AND C.STATE = #{auditState, jdbcType=VARCHAR}
AND U.STATE = #{merchantState, jdbcType=VARCHAR}
AND C.CUST_NAME LIKE CONCAT('%',#{custName},'%')
AND U.EMAIL LIKE CONCAT('%',#{email},'%')
AND U.MOBILE LIKE CONCAT('%',#{mobile},'%')
AND C.MERCHANT_CODE LIKE CONCAT('%',#{merchantCode},'%')
ORDER BY C.CREATE_TIME DESC
