mapper.select:
select settle_id, agent_id, work_date, settle_begin_date, settle_end_date, protocol_id, settle_amt, finish_date, status, memo, inst_user, inst_date, Date_format(inst_datetime,'%Y-%m-%d %H:%i:%s') inst_datetime, audit_user, Date_format(audit_datetime,'%Y-%m-%d %H:%i:%s') audit_datetime, verified_user, Date_format(verified_datetime,'%Y-%m-%d %H:%i:%s') verified_datetime from td_agent_settle
settle_id= #{settleId,jdbcType=VARCHAR}
AND agent_id = #{agentId,jdbcType=VARCHAR}
AND status=#{status,jdbcType=VARCHAR}

select * from td_pay_channel_cfg
pay_channel_code = #{payChannelCode,jdbcType=VARCHAR}
AND pay_channel_name like CONCAT('%',#{payChannelName},'%')
AND pay_channel_rate = #{payChannelRate,jdbcType=VARCHAR}

