mapper.select:
select T.ORDER_ID, T.MCH_ID, T.MCH_NAME, T.SERVICE, T.INPUT_CHARSET, T.PAGE_LANGUAGE, T.CHANNEL, T.CHANNEL_SUB, T.ZX_ORDER_ID, T.CHANNEL_ORDER_ID, T.PG_URL, T.BG_URL, T.VERSION, T.ORDER_AMT, T.TRADE_AMT, case T.CHANNEL when 'sevenpay' then ttt5.SETTLE_AMT else tt3.SETTLE_AMT end as SETTLE_AMT, T.COUPON_AMT, T.PROD_DESC, T.PAY_TYPE, T.ORDER_TIMESTAMP, T.ORDER_TIME_OUT, T.ORDER_EXPIRE, T.SIGN_TYPE, T.SIGN_MSG, T.ORDER_STATE, T.NOTIFY_TIME, T.NOTIFY_COUNT, T.NOTIFY_RESULT, T.CREATE_TIME, T.FINISH_TIME, T.REFUND_TIME, T.MODIFY_TIME, T.PAYER_CONTACT, T.PAYER_ID, T.MODIFY_ID, T.CORE_SUBMIT_STATE, T.CORE_SN, T.ORDER_CORE_RETURN_CODE, T.ORDER_CORE_RETURN_MSG, T.ORDER_CORE_RETURN_TIME, A.merchant_code, B.PAY_CHANNEL_NAME channelSubName, C.PROD_NAME from td_order T left join stcdb.td_cust_info A ON T.mch_id=A.cust_id left join td_pay_channel_cfg B on T.CHANNEL_SUB = B.PAY_CHANNEL_CODE left join td_payment_prod_for_merchant C on T.service = C.PROD_CODE
left join stcs.trans_record tt1 on T.CORE_SN = tt1.req_serial_id left join stcs.trans_record_buss_accounting tt2 on tt1.msg_id = tt2.msg_id left join stcs.acct_seven_buss_flow tt3 on tt2.id = tt3.trans_flow_id
left join stcdb.td_merchant_in ttt1 on T.ORDER_ID = ttt1.OUT_ORDER_ID left join stcdb.td_trade_bill_main ttt2 on ttt1.IN_ORDER_ID = ttt2.ORDER_ID left join stcs.trans_record ttt3 on ttt2.CORE_SN = ttt3.msg_id left join stcs.trans_record_flow ttt4 on ttt3.msg_id = ttt4.msg_id and ttt4.business_type='RECEIVE' and ttt4.acct_type='SEVEN_BUSS' left join stcs.acct_seven_buss_flow ttt5 on ttt4.id = ttt5.trans_flow_id
AND T.ORDER_ID = #{orderId,jdbcType=VARCHAR}
AND T.ZX_ORDER_ID = #{zxOrderId,jdbcType=VARCHAR}
AND T.CHANNEL_ORDER_ID = #{channelOrderId,jdbcType=VARCHAR}
AND T.service like CONCAT('%',#{payProd},'%')
AND T.channel_sub like CONCAT('%',#{payChannel},'%')
AND T.create_time >= #{createTimeB,jdbcType=VARCHAR}
AND #{createTimeE,jdbcType = VARCHAR}> T.create_time
AND DATE_FORMAT(T.finish_time,'%Y-%m-%d') >= #{finishTimeB,jdbcType=VARCHAR}
AND #{finishTimeE,jdbcType = VARCHAR}>=DATE_FORMAT(T.finish_time,'%Y-%m-%d')
AND T.order_state = #{orderState,jdbcType=VARCHAR}
AND T.order_state in ('00','90','91')
AND T.channel = #{channel,jdbcType=VARCHAR}
AND A.merchant_code = #{merchantCode,jdbcType=VARCHAR}
order by CREATE_TIME DESC

select T.ORDER_ID, T.MCH_ID, T.MCH_NAME, T.SERVICE,
T.CHANNEL, T.CHANNEL_SUB, T.ZX_ORDER_ID, T.CHANNEL_ORDER_ID,
T.VERSION, T.ORDER_AMT, T.TRADE_AMT, case T.CHANNEL when 'sevenpay' then ttt5.SETTLE_AMT else tt3.SETTLE_AMT end as SETTLE_AMT, T.COUPON_AMT, T.PROD_DESC, CASE T.PAY_TYPE WHEN '1' THEN '刷卡' WHEN 'SK' THEN '刷卡' WHEN 'SM' THEN '扫码' WHEN 'SL' THEN '刷脸' END as PAY_TYPE, T.ORDER_TIMESTAMP, T.ORDER_TIME_OUT, T.ORDER_EXPIRE, T.SIGN_TYPE, T.SIGN_MSG, CASE T.ORDER_STATE WHEN '01' THEN '创建订单' when '02' then '支付处理中' when '00' then '支付成功' when '09' then '支付失败' when '90' then '已退款' when '91' then '部分退款' when '99' then '取消' END as ORDER_STATE, T.NOTIFY_TIME, T.NOTIFY_COUNT, T.NOTIFY_RESULT, T.CREATE_TIME, T.MODIFY_TIME,
T.MODIFY_ID, T.PAYER_CONTACT, T.PAYER_ID, T.FINISH_TIME, T.REFUND_TIME,
T.CORE_SUBMIT_STATE, T.CORE_SN, T.ORDER_CORE_RETURN_CODE, T.ORDER_CORE_RETURN_MSG, T.ORDER_CORE_RETURN_TIME, A.merchant_code, B.PAY_CHANNEL_NAME channelSubName, C.PROD_NAME from td_order T left join stcdb.td_cust_info A ON T.mch_id=A.cust_id left join td_pay_channel_cfg B on T.CHANNEL_SUB = B.PAY_CHANNEL_CODE left join td_payment_prod_for_merchant C on T.service = C.PROD_CODE
left join stcs.trans_record tt1 on T.CORE_SN = tt1.req_serial_id left join stcs.trans_record_buss_accounting tt2 on tt1.msg_id = tt2.msg_id left join stcs.acct_seven_buss_flow tt3 on tt2.id = tt3.trans_flow_id
left join stcdb.td_merchant_in ttt1 on T.ORDER_ID = ttt1.OUT_ORDER_ID left join stcdb.td_trade_bill_main ttt2 on ttt1.IN_ORDER_ID = ttt2.ORDER_ID left join stcs.trans_record ttt3 on ttt2.CORE_SN = ttt3.msg_id left join stcs.trans_record_flow ttt4 on ttt3.msg_id = ttt4.msg_id and ttt4.business_type='RECEIVE' and ttt4.acct_type='SEVEN_BUSS' left join stcs.acct_seven_buss_flow ttt5 on ttt4.id = ttt5.trans_flow_id
AND T.ORDER_ID = #{orderId,jdbcType=VARCHAR}
AND T.ZX_ORDER_ID = #{zxOrderId,jdbcType=VARCHAR}
AND T.CHANNEL_ORDER_ID = #{channelOrderId,jdbcType=VARCHAR}
AND T.service like CONCAT('%',#{payProd},'%')
AND T.channel_sub like CONCAT('%',#{payChannel},'%')
AND DATE_FORMAT(T.create_time,'%Y-%m-%d') >= #{createTimeB,jdbcType=VARCHAR}
AND #{createTimeE,jdbcType = VARCHAR}>=DATE_FORMAT(T.create_time,'%Y-%m-%d')
AND DATE_FORMAT(T.finish_time,'%Y-%m-%d') >= #{finishTimeB,jdbcType=VARCHAR}
AND #{finishTimeE,jdbcType = VARCHAR}>=DATE_FORMAT(T.finish_time,'%Y-%m-%d')
AND T.order_state = #{orderState,jdbcType=VARCHAR}
AND T.order_state in ('00','90','91')
AND T.channel = #{channel,jdbcType=VARCHAR}
AND A.merchant_code = #{merchantCode,jdbcType=VARCHAR}
order by CREATE_TIME DESC

select T.REFUND_ID, T.MER_REFUND_ID, T.ORDER_ID, T.MCH_ID, T.TOTAL_AMT, T.REFUND_AMT, case C.CHANNEL when 'sevenpay' then ttt3.SETTLE_AMT-ttt3.TRANS_AMT else tt3.SETTLE_AMT-tt3.TRANS_AMT end as settleAmt, T.CHANNEL_TRANS_ID, T.CHANNEL_REFUND_ID, T.REFUND_CHANNEL, T.ERR_CODE, T.ERR_DESC, DATE_FORMAT(T.REFUND_TIME,'%Y-%m-%d %H:%m:%s') REFUND_TIME, T.REFUND_STATE, DATE_FORMAT(T.CREATE_TIME,'%Y-%m-%d %H:%m:%s') CREATE_TIME, T.CREATE_ID, C.channel, D.prod_Name, E.merchant_code from td_refund T left JOIN td_order C ON T.ORDER_ID= C.ORDER_ID LEFT JOIN td_payment_prod_for_merchant D ON D.PROD_CODE = C.SERVICE LEFT JOIN stcdb.td_cust_info E ON T.mch_id = E.cust_id
left join stcs.trans_record tt1 on T.CORE_SN = tt1.req_serial_id left join stcs.trans_record_buss_accounting tt2 on tt1.msg_id = tt2.msg_id left join stcs.acct_seven_buss_flow tt3 on tt2.id = tt3.trans_flow_id
left join stcdb.td_refund_bill ttt1 on T.MER_REFUND_ID = ttt1.REFUND_MER_ORDER_ID left join stcs.trans_record_flow ttt2 on ttt2.msg_id = ttt1.core_sn and ttt2.business_type='RECEIVE_REFUND' and ttt2.acct_type='SEVEN_BUSS' left join stcs.acct_seven_buss_flow ttt3 on ttt2.id = ttt3.trans_flow_id
AND T.refund_id =#{refundId,jdbcType=VARCHAR}
AND E.merchant_code =#{merchantCode,jdbcType=VARCHAR}
AND T.mch_id =#{mchId,jdbcType=VARCHAR}
AND DATE_FORMAT(T.create_time,'%Y-%m-%d') >=#{createTimeB,jdbcType=VARCHAR}
AND #{createTimeE,jdbcType=VARCHAR} >= DATE_FORMAT(T.create_time,'%Y-%m-%d')
AND T.refund_state =#{refundState,jdbcType=VARCHAR}
AND T.order_id =#{orderId,jdbcType=VARCHAR}
AND C.channel =#{channel,jdbcType=VARCHAR}
order by CREATE_TIME DESC

select T.REFUND_ID, T.MER_REFUND_ID, T.ORDER_ID, T.MCH_ID, T.TOTAL_AMT, T.REFUND_AMT, case C.CHANNEL when 'sevenpay' then ttt3.SETTLE_AMT-ttt3.TRANS_AMT else tt3.SETTLE_AMT-tt3.TRANS_AMT end as settleAmt, T.CHANNEL_TRANS_ID, T.CHANNEL_REFUND_ID, T.REFUND_CHANNEL, T.ERR_CODE, T.ERR_DESC, DATE_FORMAT(T.REFUND_TIME,'%Y-%m-%d %H:%m:%s') REFUND_TIME, CASE T.REFUND_STATE when '0' then '退款成功' when '1' then '退款申请' when '9' then '退款失败' end as REFUND_STATE, DATE_FORMAT(T.CREATE_TIME,'%Y-%m-%d %H:%m:%s') CREATE_TIME, T.CREATE_ID, D.prod_Name, C.channel, E.MERCHANT_CODE from td_refund T left JOIN td_order C ON T.ORDER_ID= C.ORDER_ID LEFT JOIN td_payment_prod_for_merchant D ON D.PROD_CODE = C.SERVICE LEFT JOIN stcdb.td_cust_info E ON T.mch_id = E.cust_id
left join stcs.trans_record tt1 on T.CORE_SN = tt1.req_serial_id left join stcs.trans_record_buss_accounting tt2 on tt1.msg_id = tt2.msg_id left join stcs.acct_seven_buss_flow tt3 on tt2.id = tt3.trans_flow_id
left join stcdb.td_refund_bill ttt1 on T.MER_REFUND_ID = ttt1.REFUND_MER_ORDER_ID left join stcs.trans_record_flow ttt2 on ttt2.msg_id = ttt1.core_sn and ttt2.business_type='RECEIVE_REFUND' and ttt2.acct_type='SEVEN_BUSS' left join stcs.acct_seven_buss_flow ttt3 on ttt2.id = ttt3.trans_flow_id
AND T.refund_id =#{refundId,jdbcType=VARCHAR}
AND E.merchant_code =#{merchantCode,jdbcType=VARCHAR}
AND T.mch_id =#{mchId,jdbcType=VARCHAR}
AND DATE_FORMAT(T.create_time,'%Y-%m-%d') >=#{createTimeB,jdbcType=VARCHAR}
AND #{createTimeE,jdbcType=VARCHAR} >= DATE_FORMAT(T.create_time,'%Y-%m-%d')
AND T.refund_state =#{refundState,jdbcType=VARCHAR}
AND T.order_id =#{orderId,jdbcType=VARCHAR}
AND C.channel =#{channel,jdbcType=VARCHAR}
order by CREATE_TIME DESC

SELECT T.ORDER_ID, T.MCH_ID, T.MCH_NAME, T.SERVICE, T.INPUT_CHARSET, T.PAGE_LANGUAGE, T.CHANNEL, T.CHANNEL_SUB, T.ZX_ORDER_ID, T.CHANNEL_ORDER_ID, T.PG_URL, T.BG_URL, T.VERSION, T.ORDER_AMT, T.TRADE_AMT, T.COUPON_AMT, T.PROD_DESC, T.PAY_TYPE, T.ORDER_TIMESTAMP, T.ORDER_TIME_OUT, T.ORDER_EXPIRE, T.SIGN_TYPE, T.SIGN_MSG, T.ORDER_STATE, T.NOTIFY_TIME, T.NOTIFY_COUNT, T.NOTIFY_RESULT, T.CREATE_TIME, T.FINISH_TIME, T.REFUND_TIME, T.MODIFY_TIME, T.PAYER_CONTACT, T.PAYER_ID, T.MODIFY_ID, T.CORE_SUBMIT_STATE, T.CORE_SN, T.ORDER_CORE_RETURN_CODE, T.ORDER_CORE_RETURN_MSG, T.ORDER_CORE_RETURN_TIME, A.merchant_code, B.PAY_CHANNEL_NAME channelSubName, C.PROD_NAME FROM td_order T LEFT JOIN stcdb.td_cust_info A ON T.mch_id = A.cust_id LEFT JOIN td_pay_channel_cfg B ON T.CHANNEL_SUB = B.PAY_CHANNEL_CODE LEFT JOIN td_payment_prod_for_merchant C ON T.service = C.PROD_CODE WHERE EXISTS ( SELECT 1 FROM ( SELECT I.CUST_ID FROM stcdb.td_cust_info I WHERE I.custManager = #{userId,jdbcType=VARCHAR} UNION SELECT B.CUST_ID FROM stcdb.td_cust_belong_info B WHERE B.manager_id = #{userId,jdbcType=VARCHAR} ) TT WHERE TT.cust_id = T.MCH_ID )
AND T.service like CONCAT('%',#{payProd},'%')
AND T.channel_sub like CONCAT('%',#{payChannel},'%')
AND T.create_time >= #{createTimeB,jdbcType=VARCHAR}
AND #{createTimeE,jdbcType = VARCHAR}>T.create_time
AND T.order_state = #{orderState,jdbcType=VARCHAR}
AND T.order_state in ('00','90','91')
AND T.channel = #{channel,jdbcType=VARCHAR}
AND A.merchant_code = #{merchantCode,jdbcType=VARCHAR}
ORDER BY CREATE_TIME DESC

select T.REFUND_ID, T.MER_REFUND_ID, T.ORDER_ID, T.MCH_ID, T.TOTAL_AMT, T.REFUND_AMT, T.CHANNEL_TRANS_ID, T.CHANNEL_REFUND_ID, T.REFUND_CHANNEL, T.ERR_CODE, T.ERR_DESC, DATE_FORMAT(T.REFUND_TIME,'%Y-%m-%d %H:%m:%s') REFUND_TIME, T.REFUND_STATE, DATE_FORMAT(T.CREATE_TIME,'%Y-%m-%d %H:%m:%s') CREATE_TIME, T.CREATE_ID, A.channel, B.merchant_code from td_refund T left join td_order A on T.order_id = A.order_id left join stcdb.td_cust_info B on T.mch_id = B.cust_id WHERE EXISTS ( SELECT 1 FROM ( SELECT temp.cust_id FROM stcdb.bms_protocol_content temp WHERE temp.id IN ( SELECT bms.protocol_id FROM stcdb.bms_protocol_column bms WHERE (bms.column_value = #{userName,jdbcType=VARCHAR} or bms.column_value = #{userId,jdbcType=VARCHAR}) AND bms.column_code = 'custManager' ) ) TT WHERE TT.cust_id = T.MCH_ID )
AND T.refund_id =#{refundId,jdbcType=VARCHAR}
AND B.merchant_code =#{merchantCode,jdbcType=VARCHAR}
AND T.mch_id =#{mchId,jdbcType=VARCHAR}
AND DATE_FORMAT(T.create_time,'%Y-%m-%d') >=#{createTimeB,jdbcType=VARCHAR}
AND #{createTimeE,jdbcType=VARCHAR} >= DATE_FORMAT(T.create_time,'%Y-%m-%d')
AND T.refund_state =#{refundState,jdbcType=VARCHAR}
AND T.order_id =#{orderId,jdbcType=VARCHAR}
AND A.channel =#{channel,jdbcType=VARCHAR}
order by T.CREATE_TIME DESC

select T.REFUND_ID, T.MER_REFUND_ID, T.ORDER_ID, T.MCH_ID, T.TOTAL_AMT, T.REFUND_AMT, T.CHANNEL_TRANS_ID, T.CHANNEL_REFUND_ID, T.REFUND_CHANNEL, T.ERR_CODE, T.ERR_DESC, DATE_FORMAT(T.REFUND_TIME,'%Y-%m-%d %H:%m:%s') REFUND_TIME, CASE T.REFUND_STATE when '0' then '退款成功' when '1' then '退款申请' when '9' then '退款失败' end as REFUND_STATE, DATE_FORMAT(T.CREATE_TIME,'%Y-%m-%d %H:%m:%s') CREATE_TIME, T.CREATE_ID, A.channel, B.MERCHANT_CODE from td_refund T left join td_order A on T.order_id = A.order_id left join stcdb.td_cust_info B on T.mch_id = B.cust_id WHERE EXISTS ( SELECT 1 FROM ( SELECT temp.cust_id FROM stcdb.bms_protocol_content temp WHERE temp.id IN ( SELECT bms.protocol_id FROM stcdb.bms_protocol_column bms WHERE (bms.column_value = #{userName,jdbcType=VARCHAR} or bms.column_value = #{userId,jdbcType=VARCHAR}) AND bms.column_code = 'custManager' ) ) TT WHERE TT.cust_id = T.MCH_ID )
AND T.refund_id =#{refundId,jdbcType=VARCHAR}
AND B.merchant_code =#{merchantCode,jdbcType=VARCHAR}
AND T.mch_id =#{mchId,jdbcType=VARCHAR}
AND DATE_FORMAT(T.create_time,'%Y-%m-%d') >=#{createTimeB,jdbcType=VARCHAR}
AND #{createTimeE,jdbcType=VARCHAR} >= DATE_FORMAT(T.create_time,'%Y-%m-%d')
AND T.refund_state =#{refundState,jdbcType=VARCHAR}
AND T.order_id =#{orderId,jdbcType=VARCHAR}
AND A.channel =#{channel,jdbcType=VARCHAR}
order by CREATE_TIME DESC
