mapper.select:
SELECT T.ORDER_ID, T.ORDER_NAME, T.ORDER_DESC, PAY.MERCHANT_CODE PAYER_CUST_ID, RCV.MERCHANT_CODE PAYEE_CUST_ID, T.PAYEE_CUST_NAME, T.PAYER_CUST_NAME, IFNULL(DATE_FORMAT(T.ORDER_CORE_RETURN_TIME,'%Y%m%d'),DATE_FORMAT(T.ORDER_CREATE_TIME,'%Y%m%d')) WORK_DATE, T.ORDER_CREATE_TIME, T.ORDER_STATE, T.SUM_AMT FROM TD_TRADE_BILL_MAIN T JOIN TD_CUST_INFO PAY ON T.PAYER_CUST_ID = PAY.CUST_ID JOIN TD_CUST_INFO RCV ON T.PAYEE_CUST_ID = RCV.CUST_ID
T.TRADE_TYPE = '1002'
AND T.ORDER_ID LIKE CONCAT('%',#{orderId} ,'%')
AND PAY.MERCHANT_CODE LIKE CONCAT('%', #{payerCustId} ,'%')
AND T.PAYER_CUST_NAME LIKE CONCAT('%',#{payerCustName} ,'%')
AND RCV.MERCHANT_CODE LIKE CONCAT('%', #{payeeCustId} ,'%')
AND T.PAYEE_CUST_NAME LIKE CONCAT('%',#{payeeCustName} ,'%')
AND T.ORDER_STATE = #{orderState, jdbcType = VARCHAR}
AND DATE_FORMAT(T.ORDER_CREATE_TIME ,'%Y-%m-%d') >= #{beginTime}
AND DATE_FORMAT(T.ORDER_CREATE_TIME ,'%Y-%m-%d') <= #{endTime}
AND IFNULL(DATE_FORMAT(T.ORDER_CORE_RETURN_TIME,'%Y%m%d'),DATE_FORMAT(T.ORDER_CREATE_TIME,'%Y%m%d')) >= #{startWorkDate}
AND IFNULL(DATE_FORMAT(T.ORDER_CORE_RETURN_TIME,'%Y%m%d'),DATE_FORMAT(T.ORDER_CREATE_TIME,'%Y%m%d')) <= #{endWorkDate}
ORDER BY T.ORDER_CREATE_TIME DESC

SELECT T.ORDER_ID, T.ORDER_NAME, T.ORDER_DESC, PAY.MERCHANT_CODE PAYER_CUST_ID, RCV.MERCHANT_CODE PAYEE_CUST_ID, T.PAYEE_CUST_NAME, T.PAYER_CUST_NAME, IFNULL(DATE_FORMAT(T.ORDER_CORE_RETURN_TIME,'%Y%m%d'),DATE_FORMAT(T.ORDER_CREATE_TIME,'%Y%m%d')) WORK_DATE, T.ORDER_CREATE_TIME, CASE T.ORDER_STATE WHEN '00' THEN '成功' WHEN '01' THEN '待提交' WHEN '02' THEN '提交核心' WHEN '03' THEN '核心处理中' WHEN '04' THEN '失败' WHEN '99' THEN '取消' WHEN '90' THEN '未明' ELSE T.ORDER_STATE END AS ORDER_STATE, T.SUM_AMT FROM TD_TRADE_BILL_MAIN T JOIN TD_CUST_INFO PAY ON T.PAYER_CUST_ID = PAY.CUST_ID JOIN TD_CUST_INFO RCV ON T.PAYEE_CUST_ID = RCV.CUST_ID
T.TRADE_TYPE = '1002'
AND T.ORDER_ID LIKE CONCAT('%',#{orderId} ,'%')
AND PAY.MERCHANT_CODE LIKE CONCAT('%', #{payerCustId} ,'%')
AND T.PAYER_CUST_NAME LIKE CONCAT('%',#{payerCustName} ,'%')
AND RCV.MERCHANT_CODE LIKE CONCAT('%', #{payeeCustId} ,'%')
AND T.PAYEE_CUST_NAME LIKE CONCAT('%',#{payeeCustName} ,'%')
AND T.ORDER_STATE = #{orderState, jdbcType = VARCHAR}
AND DATE_FORMAT(T.ORDER_CREATE_TIME ,'%Y-%m-%d') >= #{beginTime}
AND DATE_FORMAT(T.ORDER_CREATE_TIME ,'%Y-%m-%d') <= #{endTime}
AND IFNULL(DATE_FORMAT(T.ORDER_CORE_RETURN_TIME,'%Y%m%d'),DATE_FORMAT(T.ORDER_CREATE_TIME,'%Y%m%d')) >= #{startWorkDate}
AND IFNULL(DATE_FORMAT(T.ORDER_CORE_RETURN_TIME,'%Y%m%d'),DATE_FORMAT(T.ORDER_CREATE_TIME,'%Y%m%d')) <= #{endWorkDate}
ORDER BY T.ORDER_CREATE_TIME DESC
