mapper.select:
SELECT IFNULL(hc.num, 0) count_num, t.STATE, t.CUST_ID, t.`MERCHANT_CODE`, t.`CUST_NAME`, h.CERTIFICATE_STATE auditState, a.MOBILE, a.`EMAIL`, t.AGENT_NAME, t.CREATE_TIME, t.AUTH_ID, ( CASE t.CUST_TYPE WHEN '0' THEN '个人' WHEN '1' THEN '企业' ELSE '' END ) custType, a.`STATE` merchantState, concat( 'H5:', IFNULL(p5.column_value, '无'), '/', 'app:', IFNULL(p6.column_value, '无'), '/', '扫码:', IFNULL(p7.column_value, '无'), '/', 'PC:', IFNULL(p8.column_value, '无'), '/', '原生H5:', IFNULL(p9.column_value, '无') ) agent_rate, p1.column_value bankCardName, p2.column_value bankCardNo, p3.column_value bankName, IFNULL((SELECT t.user_name from sys_user t where t.self_phone = p4.column_value or t.user_id= p4.column_value),'无') columnValue FROM td_cust_info t LEFT JOIN td_login_user_info a ON t.CUST_ID = a.CUST_ID and a.STATE in ('04','00','01') LEFT JOIN ( SELECT t1.CUST_ID, count(t1.CUST_ID) num FROM act_workflow_merchantaudit_history ac LEFT JOIN td_cust_info t1 ON ac.merchantId = t1.CUST_ID WHERE t1.CUST_ID = ac.merchantId GROUP BY t1.CUST_ID ) hc ON hc.CUST_ID = t.cust_id LEFT JOIN bms_protocol_content b ON t.CUST_ID = b.cust_id LEFT JOIN bms_protocol_column p1 ON b.id = p1.protocol_id and p1.column_code = 'bankCardName' LEFT JOIN bms_protocol_column p2 ON b.id = p2.protocol_id and p2.column_code = 'bankCardNo' LEFT JOIN bms_protocol_column p3 ON b.id = p3.protocol_id and p3.column_code = 'bankName' LEFT JOIN bms_protocol_column p4 ON b.id = p4.protocol_id and p4.column_code = 'custManager' LEFT JOIN bms_protocol_column p5 ON b.id = p5.protocol_id AND p5.column_code = 'h5.gateway.pay_rate' AND p5.`status` = 'VALID' LEFT JOIN bms_protocol_column p6 ON b.id = p6.protocol_id AND p6.column_code = 'mobile.plugin.pay_rate' AND p6.`status` = 'VALID' LEFT JOIN bms_protocol_column p7 ON b.id = p7.protocol_id AND p7.column_code = 'onecode.coll.pay_rate' AND p7.`status` = 'VALID' LEFT JOIN bms_protocol_column p8 ON b.id = p8.protocol_id AND p8.column_code = 'pc.gateway.pay_rate' AND p8.`status` = 'VALID' LEFT JOIN bms_protocol_column p9 ON b.id = p9.protocol_id AND p9.column_code = 'h5_t.gateway.pay_rate' AND p9.`status` = 'VALID' LEFT JOIN td_certificate_auth h ON t.AUTH_ID = h.AUTH_ID
t.MERCHANT_FLAG='2' and t.CERTIFY_TYPE is not null
AND t.CREATE_TIME >= #{startCreateTime}
AND t.CREATE_TIME <= #{endCreateTime}
AND t.STATE = #{auditState}
AND a.STATE = #{merchantState, jdbcType=VARCHAR}
AND t.CUST_NAME LIKE CONCAT('%',#{custName},'%')
AND a.EMAIL LIKE CONCAT('%',#{email},'%')
AND t.MERCHANT_CODE LIKE CONCAT('%',#{merchantCode},'%')
AND a.MOBILE=#{mobile}
ORDER BY t.CREATE_TIME DESC

mapper.update:
UPDATE bms_protocol_column b
b.status= #{status},
WHERE b.protocol_id=#{protocolId}

UPDATE bms_protocol_column b
b.status= #{status},
WHERE b.id=#{id}

mapper.select:
SELECT t.MERCHANT_CODE, t.`CUST_NAME`, a.MOBILE, p1.column_value bankCardName, p2.column_value bankCardNo, a.`EMAIL` Email, t.CREATE_TIME createTime, ( CASE t.STATE WHEN '00' THEN '正常' WHEN '04' THEN '审核不通过' WHEN '01' THEN '待审核' ELSE '' END)agencyState, m.CASHIER_NAME
FROM td_cust_info t LEFT JOIN td_login_user_info a ON t.CUST_ID = a.CUST_ID INNER JOIN bms_protocol_content b ON t.CUST_ID = b.cust_id LEFT JOIN bms_protocol_column p1 ON b.id = p1.protocol_id and p1.column_code = 'bankCardName' LEFT JOIN bms_protocol_column p2 ON b.id = p2.protocol_id and p2.column_code = 'bankCardNo' LEFT JOIN td_certificate_auth h ON t.AUTH_ID = h.AUTH_ID LEFT JOIN bms_protocol_column c ON c.protocol_id = b.id AND c.column_code = 'custManager' LEFT JOIN td_merchant_cashier_ref m on m.MERCHANT_CUST_ID = t.CUST_ID
a.STATE in ('04','00','01') and t.MERCHANT_FLAG='2' and t.CERTIFY_TYPE is not null
AND DATE_FORMAT(t.CREATE_TIME ,'%Y-%m-%d')>= #{startCreateTime}
AND DATE_FORMAT(t.CREATE_TIME ,'%Y-%m-%d')<= #{endCreateTime}
AND t.STATE = #{auditState}
AND a.STATE = #{merchantState, jdbcType=VARCHAR}
AND t.CUST_NAME LIKE CONCAT('%',#{custName},'%')
AND a.EMAIL LIKE CONCAT('%',#{email},'%')
AND t.MERCHANT_CODE LIKE CONCAT('%',#{merchantCode},'%')
ORDER BY t.CREATE_TIME DESC

mapper.update:
update td_merchant_cashier_ref T
T.MODIFY_TIME = #{modifyTime},
T.REFUND_AUTH = #{refundAuth},
T.MODIFY_ID = #{modifyId}
where T.only_id = #{onlyId}

mapper.select:
select  
	c.*
from 
	category_code_info c


		c.categoryTypeId= #{categoryTypeId,jdbcType=VARCHAR}


		c.categoryId= #{categoryId,jdbcType=VARCHAR}
