mapper.select:
SELECT T.*, A.cust_name merchantName, B.cust_name createName, C.cust_name modifyName, D.shop_name shopName FROM td_merchant_cashier_ref T LEFT JOIN td_cust_info A ON T.MERCHANT_CUST_ID = A.cust_id LEFT JOIN td_cust_info B ON T.create_id = B.cust_id LEFT JOIN td_cust_info c ON T.modify_id = C.cust_id LEFT JOIN td_shop D ON t.SHOP_ID = D.SHOP_ID LEFT JOIN td_login_user_info E ON E.cust_id = T.MERCHANT_CUST_ID WHERE 1 = 1 -- AND E.state IN ('00', '01', '05', '06')
AND T.cashier_mobile=#{cashierMobile,jdbcType=VARCHAR}
AND T.MERCHANT_CUST_ID = #{merchantCustId,jdbcType=VARCHAR}
AND T.refund_auth = #{refundAuth,jdbcType=VARCHAR}
AND T.SHOP_ID = #{shopId,jdbcType=VARCHAR}
AND T.status = #{status,jdbcType=VARCHAR}
AND T.only_id != #{onlyId,jdbcType=VARCHAR}
order by T.create_time desc

SELECT T.*, A.cust_name merchantName, B.cust_name createName, C.cust_name modifyName, D.shop_name shopName FROM td_merchant_cashier_ref T LEFT JOIN td_shop D ON T.MERCHANT_CUST_ID =D.MCH_ID LEFT JOIN td_cust_info A ON T.MERCHANT_CUST_ID = A.cust_id left join td_cust_info B on T.create_id = B.cust_id left join td_cust_info c on T.modify_id = C.cust_id WHERE 1 = 1 AND ( EXISTS ( SELECT 1 FROM ( SELECT temp.cust_id FROM stcdb.bms_protocol_content temp WHERE temp.id IN ( SELECT bms.protocol_id FROM stcdb.bms_protocol_column bms WHERE (bms.column_value = #{userName,jdbcType=VARCHAR} or bms.column_value = #{userId,jdbcType=VARCHAR}) AND bms.column_code = 'custManager' ) ) TT WHERE TT.cust_id = T.MERCHANT_CUST_ID ) OR EXISTS ( SELECT * FROM td_cust_info cust WHERE cust.cust_id = T.MERCHANT_CUST_ID AND cust.create_id = #{userId,jdbcType=VARCHAR} ) )
AND T.cashier_mobile=#{cashierMobile,jdbcType=VARCHAR}
AND T.MERCHANT_CUST_ID = #{merchantCustId,jdbcType=VARCHAR}
AND T.refund_auth = #{refundAuth,jdbcType=VARCHAR}
AND T.status = #{status,jdbcType=VARCHAR}
order by T.create_time desc

mapper.update:
update 
	td_merchant_cashier_ref T


		T.cashier_mobile=#{cashierMobile,jdbcType=VARCHAR},


		T.REFUND_AUTH = #{refundAuth,jdbcType=VARCHAR},


		T.QUERY_AUTH = #{queryAuth,jdbcType=VARCHAR},


		T.cashier_name = #{cashierName,jdbcType=VARCHAR},


	    T.CASHIER_CUST_ID = #{cashierCustId,jdbcType=VARCHAR}


	    T.status = #{status,jdbcType=VARCHAR}



		T.WEB_REFUND_AUTH=#{webRefundAuth,jdbcType=VARCHAR},


		T.MACHINE_REFUND_AUTH = #{machineRefundAuth,jdbcType=VARCHAR},


		T.APP_REFUND_AUTH = #{appRefundAuth,jdbcType=VARCHAR},


		T.OFFICIAL_REFUND_AUTH = #{officialRefundAuth,jdbcType=VARCHAR},


		T.MINI_REFUND_AUTH = #{miniRefundAuth,jdbcType=VARCHAR}


		T.EXTERNAL_ID = #{externalId,jdbcType=VARCHAR}


where
    T.only_id = #{onlyId,jdbcType=VARCHAR}

mapper.select:
select 
	T.*
from
	td_merchant_cashier_ref T
where
T.ONLY_ID != #{onlyId,jdbcType=VARCHAR}

	AND T.cashier_mobile=#{cashierMobile,jdbcType=VARCHAR}


	AND T.MERCHANT_CUST_ID = #{merchantCustId,jdbcType=VARCHAR}


	AND T.CASHIER_CUST_ID = #{cashierCustId,jdbcType=VARCHAR}

order by T.create_time desc
