SELECT C.MERCHANT_CODE, C.CUST_NAME, C.TRADE_PWD, C.ATTACH_STR, U.WRONG_PWD_COUNT, C.CERTIFY_TYPE, C.CERTIFY_NO, C.CUST_TYPE, U.STATE merchantState, U.EMAIL, C.MERCHANT_FLAG, C.CUST_SCORE, C.CUST_LVL, C.TRUST_CERTIFY_LVL, C.TRUST_CERTIFY_AUDIT_STATE, C.CUST_INFO_LVL, C.CUST_ADD, C.CUST_POST_CODE, C.BUSINESS_LICENSE, C.TAX_REG_CERT, C.REPRESENTATIVE_CERT_TYPE, C.REPRESENTATIVE_CERT_NO, C.REPRESENTATIVE_NAME, C.REG_CAPITAL, C.ENT_TYPE, C.INDUSTRY_BELONG, C.YEAR_TURNOVER, C.MERCHANT_WEB_SITE, C.IS_USE_CERTIFICATION, C.IS_USE_MSG, C.QFQ_ACC_ID, C.QFB_ACC_ID, C.CREATE_ID, C.CREATE_TIME, C.MODIFY_ID, C.MODIFY_TIME, C.BUSINESS_TERM, C.BUSINESS_REG_ADDR, C.CONTACT_PHONE, C.FCONTACTUNIT_NUMBER, C.ORG_INST_CODE, C.REPRESENTATIVE_ADDR, C.REPRESENTATIVE_MOBILE, C.AGENT_NAME, C.AGENT_CERT_TYPE, C.AGENT_CERT_CODE, C.AGENT_MOBILE, C.COMP_MAIN_ACCT, C.COMP_ACCT_BANK, C.PWD_FREEZE_TIME, C.COMP_VERIFY_AMT, C.BRANCH_BANK, C.BANK_ACCT_NAME, C.COMMENT, T.CERTIFICATE_STATE auditState FROM TD_CUST_INFO C INNER JOIN TD_LOGIN_USER_INFO U ON C.CUST_ID = U.CUST_ID JOIN TD_CERTIFICATE_AUTH T ON C.AUTH_ID = T.AUTH_ID
C.CUST_TYPE = '1' AND C.MERCHANT_FLAG = '0' AND C.TRUST_CERTIFY_AUDIT_STATE in('01','30','34','35')
AND DATE_FORMAT(C.CREATE_TIME ,'%Y-%m-%d')>= #{startCreateTime}
AND DATE_FORMAT(C.CREATE_TIME ,'%Y-%m-%d')<= #{endCreateTime}
AND T.CERTIFICATE_STATE = #{auditState, jdbcType=VARCHAR}
AND U.STATE = #{merchantState, jdbcType=VARCHAR}
AND C.CUST_NAME LIKE CONCAT('%',#{custName},'%')
AND U.EMAIL LIKE CONCAT('%',#{email},'%')
AND C.MERCHANT_CODE LIKE CONCAT('%',#{merchantCode},'%')
ORDER BY C.CREATE_TIME DESC

SELECT C.MERCHANT_CODE, C.CUST_NAME, C.TRADE_PWD, C.ATTACH_STR, U.WRONG_PWD_COUNT, C.CERTIFY_TYPE, C.CERTIFY_NO, C.CUST_TYPE, U.STATE merchantState, U.EMAIL, C.MERCHANT_FLAG, C.CUST_SCORE, C.CUST_LVL, C.TRUST_CERTIFY_LVL, C.TRUST_CERTIFY_AUDIT_STATE, C.CUST_INFO_LVL, C.CUST_ADD, C.CUST_POST_CODE, C.BUSINESS_LICENSE, C.TAX_REG_CERT, C.REPRESENTATIVE_CERT_TYPE, C.REPRESENTATIVE_CERT_NO, C.REPRESENTATIVE_NAME, C.REG_CAPITAL, C.ENT_TYPE, C.INDUSTRY_BELONG, C.YEAR_TURNOVER, C.MERCHANT_WEB_SITE, C.IS_USE_CERTIFICATION, C.IS_USE_MSG, C.QFQ_ACC_ID, C.QFB_ACC_ID, C.CREATE_ID, C.CREATE_TIME, C.MODIFY_ID, C.MODIFY_TIME, C.BUSINESS_TERM, C.BUSINESS_REG_ADDR, C.CONTACT_PHONE, C.FCONTACTUNIT_NUMBER, C.ORG_INST_CODE, C.REPRESENTATIVE_ADDR, C.REPRESENTATIVE_MOBILE, C.AGENT_NAME, C.AGENT_CERT_TYPE, C.AGENT_CERT_CODE, C.AGENT_MOBILE, C.COMP_MAIN_ACCT, C.COMP_ACCT_BANK, C.PWD_FREEZE_TIME, C.COMP_VERIFY_AMT, C.BRANCH_BANK, C.BANK_ACCT_NAME, C.COMMENT, T.CERTIFICATE_STATE auditState FROM TD_CUST_INFO C INNER JOIN TD_LOGIN_USER_INFO U ON C.CUST_ID = U.CUST_ID left JOIN TD_CERTIFICATE_AUTH T ON C.AUTH_ID = T.AUTH_ID
C.CUST_TYPE = '1' AND C.MERCHANT_FLAG = '0' AND C.TRUST_CERTIFY_AUDIT_STATE = '34' AND U.STATE ='04'
AND DATE_FORMAT(C.CREATE_TIME ,'%Y-%m-%d')>= #{startCreateTime}
AND DATE_FORMAT(C.CREATE_TIME ,'%Y-%m-%d')<= #{endCreateTime}
AND T.CERTIFICATE_STATE = #{auditState, jdbcType=VARCHAR}
AND U.STATE = #{merchantState, jdbcType=VARCHAR}
AND C.CUST_NAME LIKE CONCAT('%',#{custName},'%')
AND U.EMAIL LIKE CONCAT('%',#{email},'%')
AND C.MERCHANT_CODE LIKE CONCAT('%',#{merchantCode},'%')
ORDER BY C.CREATE_TIME DESC

SELECT C.MERCHANT_CODE, C.CUST_NAME, C.TRADE_PWD, C.ATTACH_STR, U.WRONG_PWD_COUNT, C.CERTIFY_TYPE, C.CERTIFY_NO, C.CUST_TYPE, U.STATE merchantState, U.EMAIL, C.MERCHANT_FLAG, C.CUST_SCORE, C.CUST_LVL, C.TRUST_CERTIFY_LVL, C.TRUST_CERTIFY_AUDIT_STATE, C.CUST_INFO_LVL, C.CUST_ADD, C.CUST_POST_CODE, C.BUSINESS_LICENSE, C.TAX_REG_CERT, C.REPRESENTATIVE_CERT_TYPE, C.REPRESENTATIVE_CERT_NO, C.REPRESENTATIVE_NAME, C.REG_CAPITAL, C.ENT_TYPE, C.INDUSTRY_BELONG, C.YEAR_TURNOVER, C.MERCHANT_WEB_SITE, C.IS_USE_CERTIFICATION, C.IS_USE_MSG, C.QFQ_ACC_ID, C.QFB_ACC_ID, C.CREATE_ID, C.CREATE_TIME, C.MODIFY_ID, C.MODIFY_TIME, C.BUSINESS_TERM, C.BUSINESS_REG_ADDR, C.CONTACT_PHONE, C.FCONTACTUNIT_NUMBER, C.ORG_INST_CODE, C.REPRESENTATIVE_ADDR, C.REPRESENTATIVE_MOBILE, C.AGENT_NAME, C.AGENT_CERT_TYPE, C.AGENT_CERT_CODE, C.AGENT_MOBILE, C.COMP_MAIN_ACCT, C.COMP_ACCT_BANK, C.PWD_FREEZE_TIME, C.COMP_VERIFY_AMT, C.BRANCH_BANK, C.BANK_ACCT_NAME, C.COMMENT, T.CERTIFICATE_STATE auditState FROM TD_CUST_INFO C INNER JOIN TD_LOGIN_USER_INFO U ON C.CUST_ID = U.CUST_ID left JOIN TD_CERTIFICATE_AUTH T ON C.AUTH_ID = T.AUTH_ID
C.CUST_TYPE = '1' AND C.MERCHANT_FLAG = '0' AND C.TRUST_CERTIFY_AUDIT_STATE = '01'
AND DATE_FORMAT(C.CREATE_TIME ,'%Y-%m-%d')>= #{startCreateTime}
AND DATE_FORMAT(C.CREATE_TIME ,'%Y-%m-%d')<= #{endCreateTime}
AND T.CERTIFICATE_STATE = #{auditState, jdbcType=VARCHAR}
AND U.STATE = #{merchantState, jdbcType=VARCHAR}
AND C.CUST_NAME LIKE CONCAT('%',#{custName},'%')
AND U.EMAIL LIKE CONCAT('%',#{email},'%')
AND C.MERCHANT_CODE LIKE CONCAT('%',#{merchantCode},'%')
ORDER BY C.CREATE_TIME DESC

SELECT C.AUTH_ID, C.CUST_ID, C.CUST_NAME, C.TRADE_PWD, C.ATTACH_STR, C.CERTIFY_TYPE, C.CERTIFY_NO, C.CUST_TYPE, C.MERCHANT_FLAG, C.CUST_SCORE, C.CUST_LVL, C.STATE, C.STATE auditState, C.TRUST_CERTIFY_LVL, C.TRUST_CERTIFY_AUDIT_STATE, C.CUST_INFO_LVL, C.CUST_ADD, C.CUST_POST_CODE, C.BUSINESS_LICENSE, C.TAX_REG_CERT, C.REPRESENTATIVE_CERT_TYPE, C.REPRESENTATIVE_CERT_NO, C.REPRESENTATIVE_NAME, C.REG_CAPITAL, C.ENT_TYPE, C.INDUSTRY_BELONG, C.YEAR_TURNOVER, C.MERCHANT_WEB_SITE, C.IS_USE_CERTIFICATION, C.IS_USE_MSG, C.QFQ_ACC_ID, C.QFB_ACC_ID, C.CREATE_ID, C.CREATE_TIME, C.MODIFY_ID, C.MODIFY_TIME, C.BUSINESS_TERM, C.BUSINESS_REG_ADDR, C.CONTACT_PHONE, C.ORG_INST_CODE, C.REPRESENTATIVE_ADDR, C.REPRESENTATIVE_MOBILE, C.AGENT_NAME, C.AGENT_CERT_TYPE, C.AGENT_CERT_CODE, C.AGENT_MOBILE, C.COMP_MAIN_ACCT, C.COMP_ACCT_BANK, C.COMP_VERIFY_AMT, C.BRANCH_BANK, C.BANK_ACCT_NAME, C.FCONTACTUNIT_NUMBER, C.MERCHANT_CODE, C.categoryType, C.mchRole, C.province, C.city, C.country, C.filing_status, C.filing_audit_status, DATE_FORMAT(C.CREATE_TIME,'%Y-%m-%d %H:%i:%s') createTime, IFNULL(U.EMAIL,U.MOBILE) EMAIL, U.STATE merchantState, IFNULL(T.AUTH_RESULT_DESC,A.AUDIT_INFO) aduitMessage, S.USER_NAME aduitUserName FROM TD_CUST_INFO C INNER JOIN TD_LOGIN_USER_INFO U ON C.CUST_ID = U.CUST_ID JOIN TD_CERTIFICATE_AUTH T ON C.AUTH_ID = T.AUTH_ID LEFT JOIN TD_AUDIT_RECODE_INFO A ON C.MERCHANT_CODE = A.MERCHANT_CODE left JOIN SYS_USER S ON (S.USER_ID = T.CERTIFY_USER OR A.USER_ID = S.USER_ID)
((C.CUST_TYPE = '1' AND C.MERCHANT_FLAG = '0' AND C.TRUST_CERTIFY_AUDIT_STATE in('01','30','34','35')) OR (C.MERCHANT_FLAG = '2' AND C.STATE IS NOT NULL AND (A.AUDIT_TYPE = '01' or A.AUDIT_TYPE is NULL) ))
AND DATE_FORMAT(C.CREATE_TIME ,'%Y-%m-%d')>= #{startCreateTime}
AND DATE_FORMAT(C.CREATE_TIME ,'%Y-%m-%d')<= #{endCreateTime}
AND C.STATE = #{auditState, jdbcType=VARCHAR}
AND U.STATE = #{merchantState, jdbcType=VARCHAR}
AND C.CUST_NAME LIKE CONCAT('%',#{custName},'%')
AND U.EMAIL LIKE CONCAT('%',#{email},'%')
AND C.MERCHANT_CODE LIKE CONCAT('%',#{merchantCode},'%')
ORDER BY C.CREATE_TIME DESC
