mapper.select:
SELECT C.AUTH_ID, C.CUST_ID, C.CUST_NAME, C.TRADE_PWD, C.ATTACH_STR, C.CERTIFY_TYPE, C.CERTIFY_NO, C.CUST_TYPE, C.MERCHANT_FLAG, C.CUST_SCORE, C.CUST_LVL, C.STATE, C.STATE auditState, C.TRUST_CERTIFY_LVL, C.TRUST_CERTIFY_AUDIT_STATE, C.CUST_INFO_LVL, C.CUST_ADD, C.CUST_POST_CODE, C.BUSINESS_LICENSE, C.TAX_REG_CERT, C.REPRESENTATIVE_CERT_TYPE, C.REPRESENTATIVE_CERT_NO, C.REPRESENTATIVE_NAME, C.REG_CAPITAL, C.ENT_TYPE, C.INDUSTRY_BELONG, C.YEAR_TURNOVER, C.MERCHANT_WEB_SITE, C.IS_USE_CERTIFICATION, C.IS_USE_MSG, C.QFQ_ACC_ID, C.QFB_ACC_ID, C.CREATE_ID, C.CREATE_TIME, C.MODIFY_ID, C.MODIFY_TIME, C.BUSINESS_TERM, C.BUSINESS_REG_ADDR, C.CONTACT_PHONE, C.ORG_INST_CODE, C.REPRESENTATIVE_ADDR, C.REPRESENTATIVE_MOBILE, C.AGENT_NAME, C.AGENT_CERT_TYPE, C.AGENT_CERT_CODE, C.AGENT_MOBILE, C.COMP_MAIN_ACCT, C.COMP_ACCT_BANK, C.COMP_VERIFY_AMT, C.BRANCH_BANK, C.BANK_ACCT_NAME, C.FCONTACTUNIT_NUMBER, C.MERCHANT_CODE, C.MERCHANT_MOBILE agentMobile, C.categoryType, C.mchRole, C.province, C.city, C.country, C.filing_status, C.filing_audit_status, DATE_FORMAT(C.CREATE_TIME,'%Y-%m-%d %H:%i:%s') createTime, IFNULL(U.EMAIL,U.MOBILE) EMAIL, U.STATE merchantState, IFNULL(T.AUTH_RESULT_DESC,A.AUDIT_INFO) aduitMessage, S.USER_NAME aduitUserName FROM TD_CUST_INFO C INNER JOIN TD_LOGIN_USER_INFO U ON C.CUST_ID = U.CUST_ID JOIN TD_CERTIFICATE_AUTH T ON C.AUTH_ID = T.AUTH_ID LEFT JOIN TD_AUDIT_RECODE_INFO A ON C.MERCHANT_CODE = A.MERCHANT_CODE left JOIN SYS_USER S ON (S.USER_ID = T.CERTIFY_USER OR A.USER_ID = S.USER_ID)
((C.CUST_TYPE = '1' AND C.MERCHANT_FLAG = '0' AND C.TRUST_CERTIFY_AUDIT_STATE in('01','30','34','35')) OR (C.MERCHANT_FLAG = '2' AND C.STATE IS NOT NULL AND (A.AUDIT_TYPE = '01' or A.AUDIT_TYPE is NULL) ))
AND DATE_FORMAT(C.CREATE_TIME ,'%Y-%m-%d')>= #{startCreateTime}
AND DATE_FORMAT(C.CREATE_TIME ,'%Y-%m-%d')<= #{endCreateTime}
AND C.STATE = #{auditState, jdbcType=VARCHAR}
AND U.STATE = #{merchantState, jdbcType=VARCHAR}
AND C.CUST_NAME LIKE CONCAT('%',#{custName},'%')
AND U.EMAIL LIKE CONCAT('%',#{email},'%')
AND C.MERCHANT_CODE LIKE CONCAT('%',#{merchantCode},'%')
ORDER BY C.CREATE_TIME DESC

SELECT C.AUTH_ID, C.CUST_ID, C.CUST_NAME, C.TRADE_PWD, C.ATTACH_STR, C.CERTIFY_TYPE, C.CERTIFY_NO, C.CUST_TYPE, C.MERCHANT_FLAG, C.CUST_SCORE, C.CUST_LVL, C.STATE, C.STATE auditState, C.TRUST_CERTIFY_LVL, C.TRUST_CERTIFY_AUDIT_STATE, C.CUST_INFO_LVL, C.CUST_ADD, C.CUST_POST_CODE, C.BUSINESS_LICENSE, C.TAX_REG_CERT, C.REPRESENTATIVE_CERT_TYPE, C.REPRESENTATIVE_CERT_NO, C.REPRESENTATIVE_NAME, C.REG_CAPITAL, C.ENT_TYPE, C.INDUSTRY_BELONG, C.YEAR_TURNOVER, C.MERCHANT_WEB_SITE, C.IS_USE_CERTIFICATION, C.IS_USE_MSG, C.QFQ_ACC_ID, C.QFB_ACC_ID, C.CREATE_ID, C.CREATE_TIME, C.MODIFY_ID, C.MODIFY_TIME, C.BUSINESS_TERM, C.BUSINESS_REG_ADDR, C.CONTACT_PHONE, C.ORG_INST_CODE, C.REPRESENTATIVE_ADDR, C.REPRESENTATIVE_MOBILE, C.AGENT_NAME, C.AGENT_CERT_TYPE, C.AGENT_CERT_CODE, C.AGENT_MOBILE, C.COMP_MAIN_ACCT, C.COMP_ACCT_BANK, C.COMP_VERIFY_AMT, C.BRANCH_BANK, C.BANK_ACCT_NAME, C.FCONTACTUNIT_NUMBER, C.MERCHANT_CODE, C.categoryType, C.mchRole, C.province, C.city, C.country, C.filing_status, C.filing_audit_status, DATE_FORMAT(C.CREATE_TIME,'%Y-%m-%d %H:%i:%s') createTime, IFNULL(U.EMAIL,U.MOBILE) EMAIL, U.STATE merchantState, IFNULL(T.AUTH_RESULT_DESC,A.AUDIT_INFO) aduitMessage, S.USER_NAME aduitUserName FROM TD_CUST_INFO C INNER JOIN TD_LOGIN_USER_INFO U ON C.CUST_ID = U.CUST_ID JOIN TD_CERTIFICATE_AUTH T ON C.AUTH_ID = T.AUTH_ID LEFT JOIN TD_AUDIT_RECODE_INFO A ON C.MERCHANT_CODE = A.MERCHANT_CODE left JOIN SYS_USER S ON (S.USER_ID = T.CERTIFY_USER OR A.USER_ID = S.USER_ID)
((C.CUST_TYPE = '1' AND C.MERCHANT_FLAG = '0' AND C.TRUST_CERTIFY_AUDIT_STATE in('01','30','34','35')) OR (C.MERCHANT_FLAG = '2' AND C.STATE IS NOT NULL AND (A.AUDIT_TYPE = '01' or A.AUDIT_TYPE is NULL) ))
AND DATE_FORMAT(C.CREATE_TIME ,'%Y-%m-%d')>= #{startCreateTime}
AND DATE_FORMAT(C.CREATE_TIME ,'%Y-%m-%d')<= #{endCreateTime}
AND C.STATE = #{auditState, jdbcType=VARCHAR}
AND U.STATE = #{merchantState, jdbcType=VARCHAR}
AND C.CUST_NAME LIKE CONCAT('%',#{custName},'%')
AND U.EMAIL LIKE CONCAT('%',#{email},'%')
AND C.MERCHANT_CODE LIKE CONCAT('%',#{merchantCode},'%')
ORDER BY C.CREATE_TIME DESC

SELECT C.*, C.MERCHANT_MOBILE agentMobile, IFNULL(U.EMAIL,U.MOBILE) EMAIL, U.MOBILE, F.FEE_CODE, T.AUTH_RESULT_DESC aduitMessage, S.USER_NAME aduitUserName, Y.USER_NAME createName, P.PROVINCE_NAME, Q.BANK_NAME bankName, B.city_name, M.area_name as areaName, P.PROVINCE_NAME provinceName, SP.PROVINCE_NAME bankProName, SC.CITY_NAME bankCitName, d.rate agentRate, T.CERTIFY_DATETIME auditTime FROM TD_CUST_INFO C INNER JOIN TD_LOGIN_USER_INFO U ON C.CUST_ID=U.CUST_ID
JOIN TD_CERTIFICATE_AUTH T ON C.AUTH_ID = T.AUTH_ID left JOIN SYS_USER S ON S.USER_ID = T.CERTIFY_USER left JOIN SYS_USER Y ON Y.USER_ID = C.CREATE_ID left JOIN td_merchant_detail_info d ON d.merchant_code = C.merchant_code left join td_merchant_report r on r.merchant_code = C.merchant_code LEFT JOIN TD_CUST_FEE_RULE F ON C.CUST_ID=F.CUST_ID left join tb_sp_province P ON P.PROVINCE_ID = C.province left join tb_sp_city B ON B.city_id = C.city left join tb_bank_code Q ON Q.BANK_TYPE = C.COMP_ACCT_BANK left join tb_sp_area M ON M.area_id = C.country left join tb_province SP ON SP.PROVINCE_ID = C.bank_province_name left join tb_city SC ON SC.city_id = C.bank_city_name
1=1
AND d.MERCHANT_CODE = #{merchantCode, jdbcType=VARCHAR}
AND r.CHANNEL_NO = #{channelNo, jdbcType=VARCHAR}

select t.patch_no, t.merchant_code, t.report_status, t.file_status, t.out_merchant_code, t.remark, t.prov_code, t.city_code, t.contry_code, t.bank_code, t.branch_bank_code, t.branch_bank_name, t.mobile_no, t.customer_Phone, t.qq, t.create_time, t.modify_time, t.power_id, t.app_id, t.loginNo, t.bestMerchantType, t.bestpayMctNo, t.result_msg, t.zfb_batch_no, t.zfb_batch_status, t.zfb_user_id, t.zfb_auth_app_id, t.zfb_app_auth_token, t.zfb_app_refresh_token, t.zfb_expires_in, t.zfb_re_expires_in, t.rate from td_merchant_detail_info t LEFT JOIN td_merchant_report r on t.patch_no = r.patch_no
1=1
AND r.merchant_code = #{merchantCode,jdbcType=VARCHAR}
AND r.channel_no = #{channelCode,jdbcType=VARCHAR}
AND t.patch_no = #{patchNo,jdbcType=VARCHAR}
