mapper.select:
SELECT T.WITHDRAW_SN, T.BANK_ACCT_NO_INTERNAL, M.MERCHANT_CODE, T.CUST_ID, T.CUST_NAME, T.WITHDRAW_ACCT_TYPE, T.BANK_TYPE, T.BANK_CODE, T.BANK_NAME, T.BANK_ACCT_NAME, T.CURR_CODE, T.WITHDRAW_AMT, T.WITHDRAW_TYPE, T.WITHDRAW_STATE, T.FEE, T.FEE_CUST_ID, T.FEE_STATE, T.CORE_SN, T.CORE_RETURN_CODE, T.CORE_RETURN_MSG, DATE_FORMAT(T.SUBMIT_TIME,'%Y-%m-%d %H:%i:%s') AS submitTime, T.RESULT_RETURN_TIME, T.CREATE_ID, DATE_FORMAT(T.CREATE_TIME,'%Y-%m-%d %H:%i:%s') AS createTime, T.MODIFY_ID, DATE_FORMAT(T.AUDIT_TIME,'%Y-%m-%d %H:%i:%s') AS auditTime, DATE_FORMAT(T.MODIFY_TIME,'%Y-%m-%d %H:%i:%s') AS modifyTime, T.AUDIT_STATE, DATE_FORMAT(T.VERIFICATION_DATE,'%Y-%m-%d %H:%i:%s') AS verificationDate, T.VERIFICATION_STATE, T.VERIFICATION_USER FROM TD_BUSS_WITHDRAW_BILL T,TD_CUST_INFO M WHERE T.CUST_ID = M.CUST_ID
AND DATE_FORMAT(T.CREATE_TIME ,'%Y-%m-%d') >= #{beginTime,jdbcType=VARCHAR}
AND DATE_FORMAT(T.CREATE_TIME ,'%Y-%m-%d') <= #{endTime,jdbcType=VARCHAR}
AND M.MERCHANT_CODE LIKE CONCAT('%',#{merchantCode,jdbcType=VARCHAR},'%')
AND T.VERIFICATION_STATE = #{verificationState,jdbcType=VARCHAR}
AND T.WITHDRAW_SN = #{withdrawSn,jdbcType=VARCHAR}
AND T.AUDIT_STATE = #{auditState,jdbcType=VARCHAR}
AND T.WITHDRAW_STATE = #{withdrawState,jdbcType=VARCHAR}
ORDER BY T.CREATE_TIME DESC

SELECT T.WITHDRAW_SN, M.MERCHANT_CODE, T.CUST_ID, T.CUST_NAME, T.BANK_ACCT_NAME, T.BANK_CODE, T.BANK_NAME, T.WITHDRAW_AMT, T.FEE, DATE_FORMAT( T.CREATE_TIME, '%Y-%m-%d %H:%i:%s' ) AS createTime, CASE T.AUDIT_STATE WHEN '01' THEN '待审核' WHEN '02' THEN '审核通过' WHEN '04' THEN '审核不通过' END AS auditState, CASE WHEN T.WITHDRAW_STATE IN ('00') THEN '提现成功' WHEN T.WITHDRAW_STATE IN ('01') THEN '待提现' WHEN T.WITHDRAW_STATE IN ('02') THEN '核心处理中' WHEN T.WITHDRAW_STATE IN ('03') THEN '核心处理失败' WHEN T.WITHDRAW_STATE IN ('04') THEN '核心处理成功' WHEN T.WITHDRAW_STATE IN ('05') THEN '银行处理中' WHEN T.WITHDRAW_STATE IN ('99') THEN '取消' END AS withdrawState, CASE T.VERIFICATION_STATE WHEN '01' THEN '未核销' WHEN '02' THEN '已核销' END AS verificationState, T.VERIFICATION_USER, T.MODIFY_ID, DATE_FORMAT( T.AUDIT_TIME, '%Y-%m-%d %H:%i:%s' ) AS auditTime, DATE_FORMAT( T.VERIFICATION_DATE, '%Y-%m-%d %H:%i:%s' ) AS verificationDate FROM TD_BUSS_WITHDRAW_BILL T,TD_CUST_INFO M WHERE T.CUST_ID = M.CUST_ID
AND DATE_FORMAT(T.CREATE_TIME ,'%Y-%m-%d') >= #{beginTime,jdbcType=VARCHAR}
AND DATE_FORMAT(T.CREATE_TIME ,'%Y-%m-%d') <= #{endTime,jdbcType=VARCHAR}
AND M.MERCHANT_CODE LIKE CONCAT('%',#{merchantCode,jdbcType=VARCHAR},'%')
AND T.VERIFICATION_STATE = #{verificationState,jdbcType=VARCHAR}
AND T.WITHDRAW_SN = #{withdrawSn,jdbcType=VARCHAR}
AND T.AUDIT_STATE = #{auditState,jdbcType=VARCHAR}
AND T.WITHDRAW_STATE = #{withdrawState,jdbcType=VARCHAR}
ORDER BY T.CREATE_TIME DESC

update.foreach:
UPDATE TD_BUSS_WITHDRAW_BILL T
T.VERIFICATION_STATE = '02', T.VERIFICATION_DATE = NOW()
where T.WITHDRAW_SN = ${item}
