mapper.select:
SELECT F.FEE_CODE, F.FEE_TYPE, F.FEE_NAME, F.RULE_DESC, F.RULE_VALUES, F.STATUS, F.MEMO, (SELECT U.USER_NAME FROM SYS_USER U WHERE U.USER_ID =F.INST_USER) INST_USER, F.INST_TIME, (SELECT U.USER_NAME FROM SYS_USER U WHERE U.USER_ID =F.LUPD_USER) LUPD_USER, F.LUPD_TIME FROM TB_FEE_RULE F
AND F.FEE_CODE LIKE CONCAT('%',#{feeCode,jdbcType=VARCHAR},'%') AND F.FEE_NAME LIKE CONCAT('%',#{feeName,jdbcType=VARCHAR},'%') AND F.FEE_TYPE LIKE CONCAT('%',#{feeType,jdbcType=VARCHAR},'%') AND F.STATUS = #{status,jdbcType=VARCHAR}
ORDER BY F.FEE_CODE

mapper.update:
UPDATE TD_CUST_FEE_RULE
SET
	FEE_CODE = #{feeCode, 		jdbcType=VARCHAR}
WHERE
	CUST_ID = #{custId ,jdbcType=VARCHAR}
