mapper.select:
 		select 
 			T.*,
 			C.CUST_NAME	
 		from 
 			TD_AGENT_PAYMENT_BAT_DETAIL_RECORD T
 		LEFT JOIN
 			td_cust_info C
 		ON
 			T.PAER_MERCHANT_CODE = C.MERCHANT_CODE


 				and T.ORDER_NO =  #{orderNo,jdbcType = VARCHAR}


 				and T.BAT_NO =  #{batNo,jdbcType = VARCHAR}


 				and T.PAER_MERCHANT_CODE =  #{paerMerchantCode,jdbcType = VARCHAR}


 				and T.TRADE_STATUS =  #{tradeStatus,jdbcType = VARCHAR}


 				and T.ORDER_ID =  #{orderId,jdbcType = VARCHAR}


 				and T.VERIFY_STATUS =  #{verifyStatus,jdbcType = VARCHAR}


 				and T.CHANNEL_ORDER_ID =  #{channelOrderId,jdbcType = VARCHAR}


		   		and T.CREATE_TIME >= CAST(#{startCreateTime,jdbcType=VARCHAR} AS datetime)


		   		and T.CREATE_TIME <= CAST(#{endCreateTime,jdbcType=VARCHAR} AS datetime)


 		ORDER BY
 			T.CREATE_TIME DESC

select T.ORDER_NO, T.BAT_NO, T.PAER_MERCHANT_CODE, C.CUST_NAME, T.TRANS_AMT, T.Single_FeeAmt, T.ORDER_ID, T.CHANNEL_ORDER_ID, T.TRADE_STATUS, T.CREATE_TIME from TD_AGENT_PAYMENT_BAT_DETAIL_RECORD T LEFT JOIN td_cust_info C ON T.PAER_MERCHANT_CODE = C.MERCHANT_CODE
and T.ORDER_NO = #{orderNo,jdbcType = VARCHAR}
and T.BAT_NO = #{batNo,jdbcType = VARCHAR}
and T.PAER_MERCHANT_CODE = #{paerMerchantCode,jdbcType = VARCHAR}
and T.TRADE_STATUS = #{tradeStatus,jdbcType = VARCHAR}
and T.ORDER_ID = #{orderId,jdbcType = VARCHAR}
and T.VERIFY_STATUS = #{verifyStatus,jdbcType = VARCHAR}
and T.CHANNEL_ORDER_ID = #{channelOrderId,jdbcType = VARCHAR}
and T.CREATE_TIME >= CAST(#{startCreateTime,jdbcType=VARCHAR} AS datetime)
and T.CREATE_TIME <= CAST(#{endCreateTime,jdbcType=VARCHAR} AS datetime)
ORDER BY T.CREATE_TIME DESC
