mapper.select:
SELECT T.ORDER_ID, T.ORDER_NAME, T.ORDER_DESC, H.ID channelId, PAY.MOBILE PAYER_CUST_ID, RCV.MOBILE PAYEE_CUST_ID, T.PAYEE_CUST_NAME, T.PAYER_CUST_NAME, IFNULL(DATE_FORMAT(T.ORDER_CORE_RETURN_TIME,'%Y%m%d'),DATE_FORMAT(T.ORDER_CREATE_TIME,'%Y%m%d')) WORK_DATE, T.ORDER_CREATE_TIME, T.ORDER_STATE, T.SUM_AMT FROM TD_TRADE_BILL_MAIN T left JOIN TD_LOGIN_USER_INFO PAY ON T.PAYER_CUST_ID = PAY.CUST_ID left JOIN TD_LOGIN_USER_INFO RCV ON T.PAYEE_CUST_ID = RCV.CUST_ID LEFT JOIN STCS.TRANS_RECORD SS ON T.ORDER_ID = SS.REQ_SERIAL_ID LEFT JOIN ( SELECT Y.TRANS_AMT, Y.RTN_SEQ, F.MSG_ID, Y.ID, Y.WORK_DATE FROM STCS.CLEAR_JGKJ_TRANSFER Y LEFT JOIN STCS.TRANS_RECORD_FLOW F ON F.ID = Y.TRANS_FLOW_ID WHERE F.OPERATE= 'JGKJ_TRANSFER' ) H ON SS.MSG_ID = H.MSG_ID
T.TRADE_TYPE in ('1001','1302')
AND T.ORDER_ID LIKE CONCAT('%',#{orderId} ,'%')
AND H.ID = #{channelId}
AND PAY.MOBILE LIKE CONCAT('%', #{payerCustId} ,'%')
AND T.PAYER_CUST_NAME LIKE CONCAT('%',#{payerCustName} ,'%')
AND RCV.MOBILE LIKE CONCAT('%', #{payeeCustId} ,'%')
AND T.PAYEE_CUST_NAME LIKE CONCAT('%',#{payeeCustName} ,'%')
AND T.ORDER_STATE = #{orderState, jdbcType = VARCHAR}
AND T.ORDER_NAME = #{orderName, jdbcType = VARCHAR}
AND DATE_FORMAT(T.ORDER_CREATE_TIME ,'%Y-%m-%d') >= #{beginTime}
AND DATE_FORMAT(T.ORDER_CREATE_TIME ,'%Y-%m-%d') <= #{endTime}
AND IFNULL(DATE_FORMAT(T.ORDER_CORE_RETURN_TIME,'%Y%m%d'),DATE_FORMAT(T.ORDER_CREATE_TIME,'%Y%m%d')) >= #{startWorkDate}
AND IFNULL(DATE_FORMAT(T.ORDER_CORE_RETURN_TIME,'%Y%m%d'),DATE_FORMAT(T.ORDER_CREATE_TIME,'%Y%m%d')) <= #{endWorkDate}
ORDER BY T.ORDER_CREATE_TIME DESC

SELECT T.ORDER_ID, T.ORDER_NAME, T.ORDER_DESC, H.ID channelId, PAY.MOBILE PAYER_CUST_ID, RCV.MOBILE PAYEE_CUST_ID, T.PAYEE_CUST_NAME, T.PAYER_CUST_NAME, IFNULL(DATE_FORMAT(T.ORDER_CORE_RETURN_TIME,'%Y%m%d'),DATE_FORMAT(T.ORDER_CREATE_TIME,'%Y%m%d')) WORK_DATE, T.ORDER_CREATE_TIME, CASE T.ORDER_STATE WHEN '00' THEN '成功' WHEN '01' THEN '待提交' WHEN '02' THEN '提交核心' WHEN '03' THEN '核心处理中' WHEN '04' THEN '失败' WHEN '99' THEN '取消' WHEN '90' THEN '未明' ELSE T.ORDER_STATE END AS ORDER_STATE, T.SUM_AMT FROM TD_TRADE_BILL_MAIN T left JOIN TD_LOGIN_USER_INFO PAY ON T.PAYER_CUST_ID = PAY.CUST_ID left JOIN TD_LOGIN_USER_INFO RCV ON T.PAYEE_CUST_ID = RCV.CUST_ID LEFT JOIN STCS.TRANS_RECORD SS ON T.ORDER_ID = SS.REQ_SERIAL_ID LEFT JOIN ( SELECT Y.TRANS_AMT, Y.RTN_SEQ, F.MSG_ID, Y.ID, Y.WORK_DATE FROM STCS.CLEAR_JGKJ_TRANSFER Y LEFT JOIN STCS.TRANS_RECORD_FLOW F ON F.ID = Y.TRANS_FLOW_ID WHERE F.OPERATE= 'JGKJ_TRANSFER' ) H ON SS.MSG_ID = H.MSG_ID
T.TRADE_TYPE in ('1001','1302')
AND T.ORDER_ID LIKE CONCAT('%',#{orderId} ,'%')
AND H.ID = #{channelId}
AND PAY.MOBILE LIKE CONCAT('%', #{payerCustId} ,'%')
AND T.PAYER_CUST_NAME LIKE CONCAT('%',#{payerCustName} ,'%')
AND RCV.MOBILE LIKE CONCAT('%', #{payeeCustId} ,'%')
AND T.PAYEE_CUST_NAME LIKE CONCAT('%',#{payeeCustName} ,'%')
AND T.ORDER_STATE = #{orderState, jdbcType = VARCHAR}
AND DATE_FORMAT(T.ORDER_CREATE_TIME ,'%Y-%m-%d') >= #{beginTime}
AND DATE_FORMAT(T.ORDER_CREATE_TIME ,'%Y-%m-%d') <= #{endTime}
AND IFNULL(DATE_FORMAT(T.ORDER_CORE_RETURN_TIME,'%Y%m%d'),DATE_FORMAT(T.ORDER_CREATE_TIME,'%Y%m%d')) >= #{startWorkDate}
AND IFNULL(DATE_FORMAT(T.ORDER_CORE_RETURN_TIME,'%Y%m%d'),DATE_FORMAT(T.ORDER_CREATE_TIME,'%Y%m%d')) <= #{endWorkDate}
ORDER BY T.ORDER_CREATE_TIME DESC
