mapper.select:
SELECT T.ORDER_ID, T.ORIGIN_ORDER_ID, T.ORIGIN_TRANS_TIME, LU.MOBILE PAYER_CUST_ID, T.PAYER_CUST_NAME, U.MOBILE PAYEE_CUST_ID, T.PAYEE_CUST_NAME, T.CURR_CODE, T.REVOKE_AMT, T.REVOKE_MEMO, T.CORE_RETURN_MSG, (SELECT U.USER_NAME FROM SYS_USER U WHERE U.USER_ID = T.CREATE_ID ) CREATE_ID, DATE_FORMAT(T.CREATE_TIME,'%Y-%m-%d %H:%i:%s') AS CREATE_TIME, T.ORDER_STATUS, (SELECT U.USER_NAME FROM SYS_USER U WHERE U.USER_ID = T.AUDIT_ID ) AUDIT_ID, DATE_FORMAT(T.AUDIT_TIME,'%Y-%m-%d %H:%i:%s') AS AUDIT_TIME, T.AUDIT_STATE FROM TD_CUST_TRANSFER_REVOKE T,TD_LOGIN_USER_INFO LU,TD_LOGIN_USER_INFO U
T.PAYER_CUST_ID=LU.CUST_ID AND T.PAYEE_CUST_ID = U.CUST_ID
AND T.ORDER_Id = #{orderId,jdbcType = VARCHAR}
AND T.ORIGIN_ORDER_ID = #{originOrderId,jdbcType = VARCHAR}
AND LU.MOBILE = #{payerCustId,jdbcType = VARCHAR}
AND DATE_FORMAT(T.CREATE_TIME ,'%Y-%m-%d') >= #{revokeBeginTime}
AND DATE_FORMAT(T.CREATE_TIME ,'%Y-%m-%d') <= #{revokeEndTime}
ORDER BY T.CREATE_TIME DESC
