select merchant_Id, case WHEN LENGTH(merchant_name )= 6 then insert(merchant_name, 2, 1, 'X') WHEN LENGTH(merchant_name )= 9 then insert(merchant_name, 2, 1, 'X') WHEN LENGTH(merchant_name )= 12 then insert(merchant_name, 2, 2, 'XX') WHEN LENGTH(merchant_name )> 12 then insert(merchant_name, 3, 2, 'XX') ELSE merchant_Name END merchant_Name, TRADE_AMT, TRADE_TIME from merchant_trade_info
1=1
and merchant_Id = #{merchantId,jdbcType=VARCHAR}
and TRADE_TIME >= #{beginMonth,jdbcType=VARCHAR}
and TRADE_TIME <= #{endMonth,jdbcType=VARCHAR}

 	SELECT
		SUM(cast(t.trade_amt AS SIGNED))
	FROM
		merchant_trade_info t


	  			merchant_Id = #{merchantId,jdbcType=VARCHAR} 


	  			and	TRADE_TIME  >=  #{beginMonth,jdbcType=VARCHAR} 


	  			and	TRADE_TIME  <=  #{endMonth,jdbcType=VARCHAR}
