mapper.select:
SELECT T.*
  FROM BAL_FILE_RECEIVE T
 WHERE T.FILE_NAME = #{fileName,	jdbcType=VARCHAR}

    	SELECT T.*
    	  FROM BAL_FILE_RECEIVE T

			 AND T.WORK_DATE =#{workDate, jdbcType=VARCHAR}
			 AND T.FILE_TYPE = #{fileType, jdbcType=VARCHAR}
			 AND T.CHANNEL_ID = #{channelId, jdbcType=VARCHAR}
			 AND T.STATUS = #{status, jdbcType=VARCHAR}

		 ORDER BY INST_DATETIME DESC
