mapper.select:
SELECT A.*
  FROM BASE_ACTIVITY A
WHERE A.ACTIVITY_ID = #{activityId, jdbcType=INTEGER}

SELECT P.*, A.ACTIVITY_NAME, (SELECT COUNT(1) FROM BASE_PRIZE_WIN PW WHERE PW.PRIZE_ID = P.PRIZE_ID AND PW.STATUS != 'DISABLE' ) WIN_NUMBER FROM BASE_PRIZE P LEFT JOIN BASE_ACTIVITY A ON A.ACTIVITY_ID = P.ACTIVITY_ID
AND P.ACTIVITY_ID =#{activityId, jdbcType=INTEGER} AND P.STATUS =#{status, jdbcType=VARCHAR} ${dynamicSql}
ORDER BY P.PRIZE_SORT, P.PRIZE_ID DESC

SELECT PW.* 
  FROM BASE_PRIZE_WIN PW
WHERE PW.WIN_ID = #{winId, jdbcType=INTEGER}

SELECT PW.* FROM BASE_PRIZE_WIN PW
AND PW.PRIZE_ID =#{prizeId, jdbcType=INTEGER} AND PW.STATUS =#{status, jdbcType=VARCHAR}
ORDER BY PW.WIN_ID
