mapper.select:
SELECT T.WIN_ID, T.PRIZE_ID, B.PRIZE_NAME, T.WIN_CUST_ID, T.WIN_CUST_NAME, T.WIN_CUST_PHONE, T.WIN_AMOUNT, T.EFFECTIVE_DEADLINE, T.STATUS, T.INST_USER, T.INST_DATE, T.WIN_MARK, DATE_FORMAT(T.INST_DATETIME,'%Y-%m-%d %H:%i:%s') AS INST_DATETIME, T.LUPD_USER, T.LUPD_DATETIME FROM BASE_PRIZE_WIN T LEFT JOIN BASE_PRIZE B ON T.PRIZE_ID = B.PRIZE_ID
AND T.WIN_CUST_PHONE LIKE CONCAT('%',#{winCustPhone},'%')
AND B.PRIZE_NAME LIKE CONCAT('%',#{prizeName},'%')
AND T.INST_DATE =#{instDate, jdbcType=VARCHAR}
AND T.STATUS = #{status, jdbcType=VARCHAR}
ORDER BY T.LUPD_DATETIME DESC,T.WIN_ID DESC

SELECT T.WIN_ID, T.PRIZE_ID, B.PRIZE_NAME, T.WIN_CUST_ID, T.WIN_CUST_NAME, T.WIN_CUST_PHONE, T.WIN_AMOUNT, DATE_FORMAT(T.EFFECTIVE_DEADLINE,'%Y-%m-%d %H:%i:%s') AS EFFECTIVE_DEADLINE, CASE T.STATUS WHEN 'WAIT_RECEIVE' THEN '待领取' WHEN 'HAVE_RECEIVED' THEN '已领取' WHEN 'TAKE_BACK' THEN '已收回' WHEN 'DISABLE' THEN '无效' END AS STATUS, T.INST_USER, T.INST_DATE, CASE T.WIN_MARK WHEN 'BEST' THEN '最佳手气' WHEN 'WORST' THEN '最差手气' ELSE '' END AS WIN_MARK, DATE_FORMAT(T.INST_DATETIME,'%Y-%m-%d %H:%i:%s') AS INST_DATETIME, T.LUPD_USER, DATE_FORMAT(T.LUPD_DATETIME,'%Y-%m-%d %H:%i:%s') AS LUPD_DATETIME FROM BASE_PRIZE_WIN T LEFT JOIN BASE_PRIZE B ON T.PRIZE_ID = B.PRIZE_ID
AND T.WIN_CUST_PHONE LIKE CONCAT('%',#{winCustPhone},'%')
AND B.PRIZE_NAME LIKE CONCAT('%',#{prizeName},'%')
AND T.INST_DATE =#{instDate, jdbcType=VARCHAR}
AND T.STATUS = #{status, jdbcType=VARCHAR}
ORDER BY T.INST_DATETIME DESC
