mapper.select:
SELECT tc.CUST_NAME merchant_name, mm.* FROM td_cust_info tc, ( SELECT tp.product_name, tp.product_code, u.real_name, tmp.* FROM td_product_info tp inner join td_merchant_product_info tmp on tp.product_id = tmp.product_id left join sys_user u on tmp.audit_id = u.user_id
) mm
tc.MERCHANT_CODE = mm.merchant_code AND tc.CUST_NAME LIKE CONCAT('%',#{merchantName, jdbcType=VARCHAR},'%') AND mm.product_name LIKE CONCAT('%',#{productName, jdbcType=VARCHAR},'%') AND mm.product_status =#{productStatus, jdbcType=VARCHAR}
ORDER BY mm.CREATE_TIME DESC

SELECT *
FROM td_merchant_product_info

	 AND merchant_code = #{merchantCode, jdbcType=INTEGER}
	 AND product_id = #{productId, jdbcType=INTEGER}

mapper.delete:
DELETE FROM td_merchant_product_info
WHERE merchant_code = #{merchantCode,jdbcType=VARCHAR}
AND product_id = #{productId,jdbcType=VARCHAR}

mapper.select:
SELECT *
FROM td_product_info
WHERE product_id = #{productId, jdbcType=INTEGER}
