mapper.select:
select t.audit_status, t.channel_no, t.file_status, t.filing_status, t.id, t.mch_role, t.merchant_code, t.mer_msg, t.mer_status, t.patchNo, f.cust_name, DATE_FORMAT(t.modify_time, '%Y-%m-%d %H:%i:%s') modify_time, DATE_FORMAT(t.create_time, '%Y-%m-%d %H:%i:%s') create_time from td_merchant_filing_info t LEFT JOIN td_cust_info f ON t.MERCHANT_CODE=f.merchant_code
and t.merchant_code= #{merchantCode,jdbcType=VARCHAR}
and t.mch_role= #{mchRole,jdbcType=VARCHAR}
and t.channel_no = #{channelNo,jdbcType=VARCHAR}
ORDER BY t.create_time DESC

select t.audit_status, t.channel_no, t.file_status, t.filing_status, t.id, t.mch_role, t.merchant_code, t.mer_msg, t.mer_status, t.patchNo, t2.CUST_NAME, DATE_FORMAT(t.modify_time, '%Y-%m-%d') modify_time, DATE_FORMAT(t.create_time, '%Y-%m-%d') create_time from td_merchant_filing_info t left join td_cust_info t2 on t.merchant_code = t2.MERCHANT_CODE
and t.merchant_code= #{merchantCode,jdbcType=VARCHAR}
and t.mch_role= #{mchRole,jdbcType=VARCHAR}
and t.channel_no = #{channelNo,jdbcType=VARCHAR}
and t.audit_status = #{auditStatus,jdbcType=VARCHAR}
AND t2.CUST_NAME LIKE CONCAT('%',#{custName},'%')
order by t.create_time desc
