mapper.select:
select C.CUST_ID, C.CUST_NAME, C.TRADE_PWD, C.ATTACH_STR, C.CERTIFY_TYPE, C.CERTIFY_NO, (case C.CUST_TYPE WHEN '0' THEN '个人' WHEN '1' THEN '企业' ELSE '' END) CUST_TYPE, C.MERCHANT_FLAG, C.CUST_SCORE, C.CUST_LVL, C.TRUST_CERTIFY_LVL, C.TRUST_CERTIFY_AUDIT_STATE, C.CUST_INFO_LVL, C.CUST_ADD, C.CUST_POST_CODE, C.BUSINESS_LICENSE, C.TAX_REG_CERT, C.REPRESENTATIVE_CERT_TYPE, C.REPRESENTATIVE_CERT_NO, C.REPRESENTATIVE_NAME, C.REG_CAPITAL, C.ENT_TYPE, C.INDUSTRY_BELONG, C.YEAR_TURNOVER, C.MERCHANT_WEB_SITE, C.STATE, C.IS_USE_CERTIFICATION, C.IS_USE_MSG, C.QFQ_ACC_ID, C.QFB_ACC_ID, C.CREATE_ID, C.MODIFY_ID, C.MODIFY_TIME, C.BUSINESS_TERM, C.BUSINESS_REG_ADDR, C.CONTACT_PHONE, C.ORG_INST_CODE, C.REPRESENTATIVE_ADDR, C.REPRESENTATIVE_MOBILE, C.AGENT_NAME, C.AGENT_CERT_TYPE, C.AGENT_CERT_CODE, C.AGENT_MOBILE, C.COMP_MAIN_ACCT, C.COMP_ACCT_BANK, C.COMP_VERIFY_AMT, C.BRANCH_BANK, C.BANK_ACCT_NAME, C.FCONTACTUNIT_NUMBER, C.MERCHANT_CODE, C.BUSINESS_TERM_END, C.BUSINESS_TERM_START, C.REFERRER, C.SERVICE_LEVEL, DATE_FORMAT(c.CREATE_TIME,'%Y-%m-%d %H:%i:%s') CREATE_TIME, b.id protocol_id, b.disable_date, b.valid_date, u.email, u.mobile from td_cust_info c LEFT join td_login_user_info u on c.cust_id = u.cust_id LEFT join bms_protocol_content b ON c.CUST_ID = b.cust_id
c.merchant_flag = 3 and u.state in('00','01','02','05','06','07')
and c.merchant_code like CONCAT('%',#{merchantCode},'%')
and DATE_FORMAT(c.create_time,'%Y-%m-%d') >= #{startCreateTime}
and DATE_FORMAT(c.create_time,'%Y-%m-%d') <= #{endCreateTime}
and u.email like CONCAT('%',#{email},'%')
and c.cust_name like CONCAT('%',#{custName},'%')
and c.agent_name like CONCAT('%',#{agentName},'%')
and U.STATE = #{merchantState,jdbcType=VARCHAR}
and b.status = #{protocolState}
order by c.create_time desc

select C.CUST_ID, C.CUST_NAME, C.TRADE_PWD, C.ATTACH_STR, C.CERTIFY_TYPE, C.CERTIFY_NO, (case C.CUST_TYPE WHEN '0' THEN '小微企业' WHEN '1' THEN '企业' WHEN '2' THEN '个体户' ELSE '' END) CUST_TYPE, C.MERCHANT_FLAG, C.CUST_SCORE, C.CUST_LVL, C.TRUST_CERTIFY_LVL, C.TRUST_CERTIFY_AUDIT_STATE, C.CUST_INFO_LVL, C.CUST_ADD, C.CUST_POST_CODE, C.BUSINESS_LICENSE, C.TAX_REG_CERT, C.REPRESENTATIVE_CERT_TYPE, C.REPRESENTATIVE_CERT_NO, C.REPRESENTATIVE_NAME, C.REG_CAPITAL, C.ENT_TYPE, C.INDUSTRY_BELONG, C.YEAR_TURNOVER, C.MERCHANT_WEB_SITE, C.STATE, C.IS_USE_CERTIFICATION, C.IS_USE_MSG, C.QFQ_ACC_ID, C.QFB_ACC_ID, C.CREATE_ID, C.MODIFY_ID, C.MODIFY_TIME, C.BUSINESS_TERM, C.BUSINESS_REG_ADDR, C.CONTACT_PHONE, C.ORG_INST_CODE, C.REPRESENTATIVE_ADDR, C.REPRESENTATIVE_MOBILE, C.AGENT_NAME, C.AGENT_CERT_TYPE, C.AGENT_CERT_CODE, C.AGENT_MOBILE, C.COMP_MAIN_ACCT, C.COMP_ACCT_BANK, C.COMP_VERIFY_AMT, C.BRANCH_BANK, C.BANK_ACCT_NAME, C.FCONTACTUNIT_NUMBER, C.MERCHANT_CODE, C.BUSINESS_TERM_END, C.BUSINESS_TERM_START, C.REFERRER,
C.contact_name, C.contact_mobile, C.SERVICE_LEVEL, DATE_FORMAT(c.CREATE_TIME,'%Y-%m-%d %H:%i:%s') CREATE_TIME,
u.email, u.mobile from td_cust_info c LEFT join td_login_user_info u on c.cust_id = u.cust_id
c.merchant_flag = 3 and u.state in('00','01','04','02','05','06','07')
and c.merchant_code like CONCAT('%',#{merchantCode},'%')
and DATE_FORMAT(c.create_time,'%Y-%m-%d') >= #{startCreateTime}
and DATE_FORMAT(c.create_time,'%Y-%m-%d') <= #{endCreateTime}
and u.email like CONCAT('%',#{email},'%')
and c.cust_name like CONCAT('%',#{custName},'%')
and c.agent_name like CONCAT('%',#{agentName},'%')
and U.STATE = #{merchantState,jdbcType=VARCHAR}
and b.status = #{protocolState}
and c.create_time >= #{startTime}
and c.create_time <= #{endTime}
order by c.create_time desc

select c.CUST_NAME, DATE_FORMAT(c.CREATE_TIME,'%Y-%m-%d %H:%i:%s') CREATE_TIME, C.ATTACH_STR, C.CERTIFY_TYPE, C.CERTIFY_NO, (case C.CUST_TYPE WHEN '0' THEN '小薇企业' WHEN '1' THEN '企业' WHEN '2' THEN '个体户' ELSE '' END) CUST_TYPE, C.MERCHANT_FLAG, C.CUST_SCORE, C.CUST_LVL, C.TRUST_CERTIFY_LVL, C.TRUST_CERTIFY_AUDIT_STATE, C.CUST_INFO_LVL, C.CUST_ADD, C.CUST_POST_CODE, C.BUSINESS_LICENSE, C.TAX_REG_CERT, C.REPRESENTATIVE_CERT_TYPE, C.REPRESENTATIVE_CERT_NO, C.REPRESENTATIVE_NAME, C.REG_CAPITAL, C.ENT_TYPE, C.INDUSTRY_BELONG, C.YEAR_TURNOVER, C.MERCHANT_WEB_SITE, C.STATE, C.IS_USE_CERTIFICATION, C.IS_USE_MSG, C.QFQ_ACC_ID, C.QFB_ACC_ID, C.CREATE_ID, C.MODIFY_ID, C.MODIFY_TIME, C.BUSINESS_TERM, C.BUSINESS_REG_ADDR, C.CONTACT_PHONE, C.ORG_INST_CODE, C.REPRESENTATIVE_ADDR, C.REPRESENTATIVE_MOBILE, C.AGENT_NAME, C.AGENT_CERT_TYPE, C.AGENT_CERT_CODE, C.AGENT_MOBILE, C.COMP_MAIN_ACCT, C.COMP_ACCT_BANK, C.COMP_VERIFY_AMT, C.BRANCH_BANK, C.BANK_ACCT_NAME, C.FCONTACTUNIT_NUMBER, C.MERCHANT_CODE, C.BUSINESS_TERM_END, C.BUSINESS_TERM_START, C.REFERRER, C.contact_name, C.contact_mobile, C.SERVICE_LEVEL, u.email, u.mobile from td_cust_info c LEFT join td_login_user_info u on c.cust_id = u.cust_id where 1=1 and (EXISTS( SELECT 1 FROM sys_user u WHERE u.user_id =c.CREATE_ID AND u.user_id = #{userId,jdbcType=VARCHAR} ) ) and c.merchant_flag = 3 and u.state in('00','01','02','04','05','06','07')
and c.merchant_code like CONCAT('%',#{merchantCode},'%')
and DATE_FORMAT(c.create_time,'%Y-%m-%d') >= #{startCreateTime}
and DATE_FORMAT(c.create_time,'%Y-%m-%d') <= #{endCreateTime}
and u.email like CONCAT('%',#{email},'%')
and c.cust_name like CONCAT('%',#{custName},'%')
and c.agent_name like CONCAT('%',#{agentName},'%')
and c.create_time >= #{startTime}
and c.create_time <= #{endTime}
order by c.create_time desc

mapper.update:
    	 UPDATE td_cust_info c 
			SET c.STATE=#{newState}
		 WHERE c.CUST_ID = #{custId,jdbcType = VARCHAR}

		     AND c.STATE= #{state,jdbcType = VARCHAR}

    	 UPDATE td_certificate_auth c 
			SET c.CERTIFICATE_STATE=#{newState}
		 WHERE c.CUST_ID = #{custId,jdbcType = VARCHAR}

				 and c.CERTIFICATE_STATE =  #{state,jdbcType = VARCHAR}

    	 UPDATE td_login_user_info c 
			SET c.STATE=#{newState}
		 WHERE c.CUST_ID = #{custId,jdbcType = VARCHAR}

			 and c.STATE =  #{state,jdbcType = VARCHAR}
