mapper.select:
SELECT t.BAT_NO, t.PAER_ACCT_NO, t.SUM_AMT, t.SUCC_AMT, t.SUM_COUNT, t.SUCC_COUNT, t.FAIL_AMT, t.FAIL_COUNT, t.BAT_STATUS, t.CREATE_ID, s.user_name, DATE_FORMAT(t.CREATE_TIME, '%Y-%m-%d') AS createTime FROM td_agent_payment_bat_info t LEFT JOIN sys_user s ON t.CREATE_ID = s.user_id
AND DATE_FORMAT(t.CREATE_TIME ,'%Y-%m-%d')>= #{createTime}
AND DATE_FORMAT(t.CREATE_TIME ,'%Y-%m-%d')<= #{endCreateTime}
AND t.BAT_NO = #{batNo}
AND t.PAER_ACCT_NO=#{paerAcctNo}
AND t.BAT_STATUS = #{batStatus}
ORDER BY t.CREATE_TIME DESC

SELECT t.BAT_NO, t.ROW_NO, t.REC_ACCOUNT_NAME, t.REC_ACCOUNT_NO, t.REC_BANK_CNAPS, t.REC_BANK_CODE, t.TRANS_AMT, t.STATUS, t.PROC_MEMO, t.CREATE_ID, s.user_name AS createId, DATE_FORMAT(t.CREATE_TIME, '%Y-%m-%d') AS createTime FROM td_agent_payment_bat_detail_info t LEFT JOIN sys_user s ON t.CREATE_ID = s.user_id
WHERE
t.BAT_NO = #{batNo}

SELECT t.BAT_NO, t.PAER_ACCT_NO, t.SUM_AMT, t.SUCC_AMT, t.SUM_COUNT, t.SUCC_COUNT, t.FAIL_AMT, t.FAIL_COUNT, ( CASE t.BAT_STATUS WHEN '00' THEN '银行处理完成' WHEN '01' THEN '未提交' WHEN '02' THEN '已提交' WHEN '03' THEN '银行处理中' ELSE '' END)batStatus, DATE_FORMAT(t.CREATE_TIME, '%Y-%m-%d') AS createTime FROM td_agent_payment_bat_info t LEFT JOIN sys_user s ON t.CREATE_ID = s.user_id
AND DATE_FORMAT(t.CREATE_TIME ,'%Y-%m-%d')>= #{createTime}
AND DATE_FORMAT(t.CREATE_TIME ,'%Y-%m-%d')<= #{endCreateTime}
AND t.BAT_NO = #{batNo}
AND t.PAER_ACCT_NO=#{paerAcctNo}
AND t.BAT_STATUS = #{batStatus}
ORDER BY t.CREATE_TIME DESC

SELECT t.BAT_NO, t.ROW_NO, t.REC_ACCOUNT_NAME, t.REC_ACCOUNT_NO, t.REC_BANK_CNAPS, t.REC_BANK_CODE, t.TRANS_AMT, t.STATUS, t.PROC_MEMO, t.CREATE_ID, s.user_name AS createId, DATE_FORMAT(t.CREATE_TIME, '%Y-%m-%d') AS createTime FROM td_agent_payment_bat_detail_info t LEFT JOIN sys_user s ON t.CREATE_ID = s.user_id
t.BAT_NO = #{batNo}
AND DATE_FORMAT(t.CREATE_TIME ,'%Y-%m-%d')>= #{createTime}
AND DATE_FORMAT(t.CREATE_TIME ,'%Y-%m-%d')<= #{endCreateTime}
AND t.BAT_NO = #{batNo}
AND t.REC_ACCOUNT_NAME LIKE CONCAT('%',#{recAccountName},'%')
AND t.STATUS = #{status}
