mapper.select:
SELECT J.JOB_NAME, L.EXECUTE_DATE, L.JOB_CODE, L.PARAMETR, L.HOSTNAME, L.START_DATETIME, END_DATETIME, ( CASE L.EXECUTE_FLAG WHEN 'EXECUTING' THEN '执行中' WHEN 'SUCCESS' THEN '成功' WHEN 'FAILURE' THEN '失败' WHEN 'EMPTY' THEN '参数为空' ELSE 0 END ) AS EXECUTE_FLAG2, EXECUTE_FLAG, ( CASE L.EXECUTE_TYPE WHEN 'HANDLE' THEN '手动' WHEN 'AUTO' THEN '自动' ELSE 0 END ) AS EXECUTE_TYPE2, L.EXECUTE_TYPE, L.EXECUTE_DESC, L.EXECUTE_OPER FROM STCDB.BMS_SCHEDULER_LOG L LEFT JOIN STCDB.BMS_SCHEDULER_JOB J ON L.JOB_CODE = J.ID WHERE 1=1
AND L.EXECUTE_DATE = #{executeDate,jdbcType=DATE}
AND L.EXECUTE_TYPE = #{executeType,jdbcType=VARCHAR}
AND L.EXECUTE_FLAG = #{executeFlag,jdbcType=VARCHAR}
AND J.JOB_NAME LIKE CONCAT('%',#{jobName,jdbcType=VARCHAR},'%')
ORDER BY L.START_DATETIME DESC
