mapper.select:
SELECT T.ID, T.CUST_NAME, T.CUST_AMT, T.MOBILE, T.DATE_DATA, T.CONTENT, T.STATUS, (SELECT U.USER_NAME FROM SYS_USER U WHERE U.USER_ID = T.INST_USER) INST_USER, DATE_FORMAT(T.INST_DATETIME,'%Y-%m-%d %H:%i:%s') INST_DATETIME, DATE_FORMAT(T.SEND_TIME,'%Y-%m-%d %H:%i:%s') SEND_TIME FROM BASE_MESSAGE_DETAIL T
AND T.MOBILE = #{mobile,jdbcType = VARCHAR}
AND T.CUST_NAME LIKE CONCAT('%',#{custName},'%')
AND T.STATUS = #{status,jdbcType = VARCHAR}
AND T.DATE_DATA >= #{sendStartTime}
AND T.DATE_DATA <= #{sendEndTime}
ORDER BY T.INST_DATETIME DESC

mapper.delete:
