mapper.select:
SELECT T.BATCH_ID, T.BAL_DATE, T.WORK_DATE, CASE T.SYSTEM WHEN 'SEVEN' THEN '七分钱' WHEN 'REDENV' THEN '红包' ELSE T.SYSTEM END SYSTEM, CASE T.TRANS_STATUS WHEN 'SUCCESS' THEN '成功' WHEN 'DEALING' THEN '待入账' WHEN 'FAILURE' THEN '失败' ELSE T.TRANS_STATUS END TRANS_STATUS, CASE T.TRANS_TYPE WHEN 'RECHARGE' THEN '红包入账' WHEN 'PAYMENT' THEN '红包支付' WHEN 'REFUND' THEN '红包退款' ELSE T.TRANS_TYPE END TRANS_TYPE, T.TOTAL_COUNT, T.TOTAL_AMT, T.SELF_DOUBT_COUNT, T.SELF_DOUBT_AMT, T.BAL_DOUBT_COUNT, T.BAL_DOUBT_AMT, T.BAL_ERROR_COUNT, T.BAL_ERROR_AMT, T.BAL_EQUAL_COUNT, T.BAL_EQUAL_AMT, T.MEMO FROM BAL_REDENV_RESULT_STATISTIC T WHERE T.STATUS = 'VALID'
AND T.TRANS_TYPE = #{transType,jdbcType = VARCHAR}
AND T.BAL_DATE = #{balDate,jdbcType = VARCHAR}
AND T.WORK_DATE >= #{workBeginDate}
AND T.WORK_DATE<= #{workEndDate}
ORDER BY T.BATCH_ID DESC, T.TRANS_TYPE, T.TRANS_STATUS, T.SYSTEM

SELECT A.BATCH_ID, A.INST_DATE, A.CLEAR_ID, A.WORK_DATE, A.CUST_ID, CASE A.TRANS_TYPE WHEN '1301' THEN '红包支付' WHEN '1302' THEN '红包入账' WHEN '1303' THEN '红包退款' ELSE A.TRANS_TYPE END TRANS_TYPE, A.TRANS_AMT, CASE A.TRANS_STATUS WHEN '00' THEN '成功' WHEN '99' THEN '失败' ELSE A.TRANS_STATUS END TRANS_STATUS, A.RESERVE, A.BAL_MEMO FROM BAL_REDENV_BATCH T INNER JOIN BAL_REDENV_RESULT_EQUAL A ON A.BATCH_ID = T.BATCH_ID AND T.STATUS='BAL_OVER' INNER JOIN BAL_REDENV_SEVEN_RESULT_EQUAL B ON B.BATCH_ID = T.BATCH_ID AND T.STATUS='BAL_OVER' AND A.CLEAR_ID = B.CLEAR_ID
AND A.CLEAR_ID = #{clearId,jdbcType = VARCHAR}
AND A.INST_DATE = #{instDate,jdbcType = VARCHAR}
AND A.WORK_DATE >= #{workBeginDate}
AND A.WORK_DATE<= #{workEndDate}
ORDER BY A.INST_DATE DESC

SELECT A.BATCH_ID, A.INST_DATE, A.CLEAR_ID, A.WORK_DATE, A.CUST_ID, CASE A.TRANS_TYPE WHEN '1301' THEN '红包支付' WHEN '1302' THEN '红包入账' WHEN '1303' THEN '红包退款' ELSE A.TRANS_TYPE END TRANS_TYPE, A.TRANS_AMT, CASE A.TRANS_STATUS WHEN '00' THEN '成功' WHEN '99' THEN '失败' WHEN '01' THEN '待入账' ELSE A.TRANS_STATUS END TRANS_STATUS, A.RESERVE, A.BAL_MEMO FROM BAL_REDENV_BATCH T INNER JOIN BAL_REDENV_RESULT_EXCEPTION A ON A.BATCH_ID = T.BATCH_ID AND T.STATUS='BAL_OVER' AND A.BAL_RESULT='BAL_ERROR' LEFT JOIN BAL_REDENV_SEVEN_RESULT_EXCEPTION B ON B.BATCH_ID = T.BATCH_ID AND T.STATUS='BAL_OVER' AND B.BAL_RESULT='BAL_ERROR' AND A.CLEAR_ID = B.CLEAR_ID
AND A.CLEAR_ID = #{clearId,jdbcType = VARCHAR}
AND A.INST_DATE = #{instDate,jdbcType = VARCHAR}
AND A.WORK_DATE >= #{workBeginDate}
AND A.WORK_DATE<= #{workEndDate}
ORDER BY A.INST_DATE DESC

SELECT A.BATCH_ID, A.INST_DATE, A.CLEAR_ID, A.WORK_DATE, A.CUST_ID, CASE A.TRANS_TYPE WHEN '1301' THEN '红包支付' WHEN '1302' THEN '红包入账' WHEN '1303' THEN '红包退款' ELSE A.TRANS_TYPE END TRANS_TYPE, A.TRANS_AMT, CASE A.TRANS_STATUS WHEN '00' THEN '成功' WHEN '01' THEN '待入账' WHEN '99' THEN '失败' ELSE A.TRANS_STATUS END TRANS_STATUS, A.RESERVE, A.BAL_MEMO FROM BAL_REDENV_BATCH T INNER JOIN BAL_REDENV_RESULT_EXCEPTION A ON A.BATCH_ID = T.BATCH_ID AND T.STATUS = 'BAL_OVER' AND A.BAL_RESULT IN ('SELF_DOUBT','BAL_DOUBT')
AND A.CLEAR_ID = #{clearId,jdbcType = VARCHAR}
AND A.INST_DATE = #{instDate,jdbcType = VARCHAR}
AND A.WORK_DATE >= #{workBeginDate}
AND A.WORK_DATE<= #{workEndDate}
ORDER BY A.INST_DATE DESC

SELECT A.BATCH_ID, A.INST_DATE, A.CLEAR_ID, A.WORK_DATE, A.CUST_ID, CASE A.TRANS_CODE WHEN '1301' THEN '红包支付' WHEN '1302' THEN '红包入账' WHEN '1303' THEN '红包退款' ELSE A.TRANS_CODE END TRANS_CODE, A.TRANS_AMT, CASE A.TRANS_STATUS WHEN '00' THEN '成功' WHEN '99' THEN '失败' ELSE A.TRANS_STATUS END TRANS_STATUS, A.BAL_MEMO FROM BAL_REDENV_BATCH T INNER JOIN BAL_REDENV_SEVEN_RESULT_EXCEPTION A ON A.BATCH_ID = T.BATCH_ID AND T.STATUS = 'BAL_OVER' AND A.BAL_RESULT IN ('SELF_DOUBT','BAL_DOUBT')
AND A.CLEAR_ID = #{clearId,jdbcType = VARCHAR}
AND A.INST_DATE = #{instDate,jdbcType = VARCHAR}
AND A.WORK_DATE >= #{workBeginDate}
AND A.WORK_DATE<= #{workEndDate}
ORDER BY A.INST_DATE DESC

SELECT T.BATCH_ID, T.BAL_DATE, T.WORK_DATE, T.REDENV_DATE, T.REDENV_ID, T.REDENV_COUNT, T.REDENV_AMT, CASE T.TRANS_STATUS WHEN '00' THEN '成功' WHEN '99' THEN '失败' ELSE T.TRANS_STATUS END TRANS_STATUS, T.IN_SUCCESS_COUNT, T.IN_SUCCESS_AMT, T.IN_FAILURE_COUNT, T.IN_FAILURE_AMT, T.NOT_IN_COUNT, T.NOT_IN_AMT, T.REFUND_AMT, CASE T.REFUND_STATUS WHEN '00' THEN '成功' WHEN '99' THEN '失败' ELSE T.REFUND_STATUS END REFUND_STATUS, CASE T.BAL_STATUS WHEN 'EXCEPTION' THEN '异常' WHEN 'REUAL' THEN '一致' ELSE T.BAL_STATUS END BAL_STATUS, T.BAL_MEMO, CASE T.DEAL_STATUS WHEN '01' THEN '待处理' WHEN '00' THEN '处理成功' WHEN '99' THEN '无需处理' ELSE T.DEAL_STATUS END DEAL_STATUS_DESC, T.DEAL_STATUS, T.DEAL_USER, DATE_FORMAT(T.DEAL_DATETIME,'%Y-%m-%d %H:%i:%s') DEAL_DATETIME, T.DEAL_MEMO, DATE_FORMAT(T.UPDATE_DATETIME,'%Y-%m-%d %H:%i:%s') UPDATE_DATETIME FROM BAL_REDENV_RESULT_SUMMARY T
T.STATUS = 'BAL_OVER'
AND T.REDENV_ID = #{redenvId,jdbcType = VARCHAR}
AND T.BAL_DATE = #{balDate,jdbcType = VARCHAR}
AND T.REDENV_DATE = #{redenvDate,jdbcType = VARCHAR}
AND T.TRANS_STATUS = #{transStatus,jdbcType = VARCHAR}
AND T.REFUND_STATUS = #{refundStatus,jdbcType = VARCHAR}
AND T.BAL_STATUS = #{balStatus,jdbcType = VARCHAR}
AND T.DEAL_STATUS = #{dealStatus,jdbcType = VARCHAR}
AND T.WORK_DATE >= #{workBeginDate}
AND T.WORK_DATE<= #{workEndDate}
ORDER BY T.BAL_DATE DESC
