mapper.select:
SELECT T.RED_ENV_ID, T.RED_ENV_TITLE, T.RED_ENV_AMT, T.RED_ENV_COUNT, CASE T.RED_ENV_TYPE WHEN '0' THEN '随机金额' WHEN '1' THEN '固定金额' ELSE T.RED_ENV_TYPE END RED_ENV_TYPE, T.RAN_SEED, T.VALID_SECONDS, CASE T.ORDER_STATE WHEN '00' THEN '成功' WHEN '01' THEN '待支付' WHEN '02' THEN '提交处理' WHEN '03' THEN '处理失败' WHEN '99' THEN '取消' WHEN '90' THEN '未明' ELSE T.ORDER_STATE END ORDER_STATE, T.ORDER_ID, T.PICK_COUNT, T.EXPIRED_LEFT_AMT, DATE_FORMAT(T.EXPIRED_BAL_SUBMIT_TIME,'%Y-%m-%d %H:%i:%s') AS EXPIRED_BAL_SUBMIT_TIME, T.EXPIRED_BAL_PROC_ORDER_ID, CASE T.EXPIRED_STATUS WHEN '0' THEN '未过期' WHEN '1' THEN '已过期' ELSE T.EXPIRED_STATUS END EXPIRED_STATUS, CASE T.EXPIRED_BAL_PROC_STATUS WHEN '00' THEN '处理成功' WHEN '01' THEN '待处理' WHEN '02' THEN '处理失败' ELSE T.EXPIRED_BAL_PROC_STATUS END EXPIRED_BAL_PROC_STATUS, U.MOBILE AS CREATE_ID, DATE_FORMAT(T.CREATE_TIME,'%Y-%m-%d %H:%i:%s') AS CREATE_TIME, T.MODIFY_ID, DATE_FORMAT(T.MODIFY_TIME,'%Y-%m-%d %H:%i:%s') AS MODIFY_TIME FROM SNS_TD_RED_ENVELOPE_INFO T LEFT JOIN TD_LOGIN_USER_INFO U ON T.CREATE_ID = U.CUST_ID
AND T.RED_ENV_ID = #{redEnvId,jdbcType = VARCHAR}
AND T.ORDER_ID = #{orderId,jdbcType = VARCHAR}
AND T.EXPIRED_BAL_PROC_ORDER_ID = #{expiredBalProcOrderId,jdbcType = VARCHAR}
AND T.EXPIRED_STATUS = #{expiredStatus,jdbcType = VARCHAR}
AND T.EXPIRED_BAL_PROC_STATUS = #{expiredBalProcStatus,jdbcType = VARCHAR}
AND U.MOBILE = #{createId,jdbcType = VARCHAR}
AND T.RED_ENV_TYPE = #{redEnvType,jdbcType = VARCHAR}
AND T.ORDER_STATE = #{orderState,jdbcType = VARCHAR}
AND DATE_FORMAT(T.CREATE_TIME ,'%Y-%m-%d') >= #{createBeginTime}
AND DATE_FORMAT(T.CREATE_TIME ,'%Y-%m-%d') <= #{createEndTime}
ORDER BY T.CREATE_TIME DESC
