mapper.select:
SELECT T.RED_ENV_ID, U.MOBILE AS CUST_ID, T.CUST_NAME, DATE_FORMAT(T.PICK_TIME,'%Y-%m-%d %H:%i:%s') AS PICK_TIME, T.PICK_AMT, T.GREETING, CASE T.IS_LUCKY WHEN '1' THEN '是' ELSE '否' END IS_LUCKY, CASE T.IN_ORDER_STATE WHEN '00' THEN '成功' WHEN '01' THEN '待支付' WHEN '02' THEN '提交处理' WHEN '03' THEN '处理失败' WHEN '04' THEN '金额超上限' WHEN '99' THEN '取消' WHEN '90' THEN '未明' ELSE T.IN_ORDER_STATE END IN_ORDER_STATE, T.IN_ORDER_ID, DATE_FORMAT(T.IN_SUBMIT_TIME,'%Y-%m-%d %H:%i:%s') IN_SUBMIT_TIME, T.IN_ACCOUNT_FAIL_INFO, DATE_FORMAT(T.CREATE_TIME,'%Y-%m-%d %H:%i:%s') AS CREATE_TIME, DATE_FORMAT(T.MODIFY_TIME,'%Y-%m-%d %H:%i:%s') AS MODIFY_TIME FROM SNS_TD_PICK_REDENV_DETAIL T LEFT JOIN TD_LOGIN_USER_INFO U ON T.CUST_ID = U.CUST_ID WHERE T.RED_ENV_ID = #{redEnvId,jdbcType = VARCHAR}
AND U.MOBILE = #{custId,jdbcType = VARCHAR}
AND T.IN_ORDER_ID = #{inOrderId,jdbcType = VARCHAR}
ORDER BY T.CREATE_TIME DESC

SELECT T.RED_ENV_ID, U.MOBILE AS CUST_ID, T.CUST_NAME, DATE_FORMAT(T.PICK_TIME,'%Y-%m-%d %H:%i:%s') AS PICK_TIME, T.PICK_AMT, T.GREETING, CASE T.IS_LUCKY WHEN '1' THEN '是' ELSE '否' END IS_LUCKY, CASE T.IN_ORDER_STATE WHEN '00' THEN '成功' WHEN '01' THEN '待支付' WHEN '02' THEN '提交处理' WHEN '03' THEN '处理失败' WHEN '04' THEN '金额超上限' WHEN '99' THEN '取消' WHEN '90' THEN '未明' ELSE T.IN_ORDER_STATE END IN_ORDER_STATE, T.IN_ORDER_ID, DATE_FORMAT(T.IN_SUBMIT_TIME,'%Y-%m-%d %H:%i:%s') IN_SUBMIT_TIME, T.IN_ACCOUNT_FAIL_INFO, DATE_FORMAT(T.CREATE_TIME,'%Y-%m-%d %H:%i:%s') AS CREATE_TIME, DATE_FORMAT(T.MODIFY_TIME,'%Y-%m-%d %H:%i:%s') AS MODIFY_TIME FROM SNS_TD_PICK_REDENV_DETAIL T LEFT JOIN TD_LOGIN_USER_INFO U ON T.CUST_ID = U.CUST_ID
AND T.RED_ENV_ID = #{redEnvId,jdbcType = VARCHAR}
AND U.MOBILE = #{custId,jdbcType = VARCHAR}
AND T.IN_ORDER_ID = #{inOrderId,jdbcType = VARCHAR}
AND DATE_FORMAT(T.PICK_TIME ,'%Y-%m-%d') >= #{pickBeginTime}
AND DATE_FORMAT(T.PICK_TIME ,'%Y-%m-%d') <= #{pickEndTime}
ORDER BY T.PICK_TIME DESC
