mapper.select:
SELECT t1.*, t2.CUST_NAME, t2.CUST_ID, t3.APP_ID, t3.APP_DOWNLOAD_ADDRESS, t3.APP_NAME, t3.QC_APP_ID, t3.QC_PLAY, t3.SERVICE_NUMBER, t2.register_way, t2.CUST_ADD as representativeAddr, t3.WEBSITE, (SELECT a.AUDIT_INFO FROM ( SELECT AUDIT_INFO, AUDIT_STATUS, AUDIT_TIME, AUDIT_TYPE, ID, MERCHANT_CODE, USER_ID,( case when AUDIT_TYPE = '05' then 'OA_PAY' when AUDIT_TYPE = '02' then 'SCAN_PAY' when AUDIT_TYPE = '03' then 'H5_PAY' when AUDIT_TYPE = '06' then 'MINI_PAY' when AUDIT_TYPE = '04' then 'APP_PAY' when AUDIT_TYPE = '07' then 'BLUESEA_PAY' else '0' end )type FROM TD_AUDIT_RECODE_INFO ORDER BY TD_AUDIT_RECODE_INFO.AUDIT_TIME DESC ) a where type != '0' and a.MERCHANT_CODE = t1.merchant_code and a.type = t1.product_code GROUP BY a.merchant_code,a.audit_type) as auditInfo FROM td_customer_product_info t1 INNER JOIN td_cust_info t2 on t1.merchant_code = t2.MERCHANT_CODE INNER JOIN td_authorized_info t3 on t3.MERCHANT_CODE = t1.MERCHANT_CODE WHERE t1.sign_status in ('02','04','00') and t2.STATE = '00' and t1.cust_type='0'
AND t1.merchant_code =#{merchantCode, jdbcType = VARCHAR}
AND t1.product_code =#{productCode, jdbcType = VARCHAR}
AND t2.CUST_NAME LIKE CONCAT('%',#{custName, jdbcType = VARCHAR},'%')
ORDER BY t1.create_time DESC

SELECT t1.*, t2.CUST_NAME, t3.APP_ID, t3.APP_DOWNLOAD_ADDRESS, t3.APP_NAME, t3.QC_APP_ID, t3.QC_PLAY, t3.SERVICE_NUMBER, t2.register_way, t2.REPRESENTATIVE_ADDR, t3.WEBSITE FROM td_customer_product_info t1 INNER JOIN td_cust_info t2 on t1.merchant_code = t2.MERCHANT_CODE INNER JOIN td_authorized_info t3 on t3.MERCHANT_CODE = t1.MERCHANT_CODE WHERE t1.sign_status = '01' and t2.STATE = '00' and t1.cust_type='0'
AND t1.merchant_code =#{merchantCode, jdbcType = VARCHAR}
AND t1.product_code =#{productCode, jdbcType = VARCHAR}
AND t2.CUST_NAME LIKE CONCAT('%',#{custName, jdbcType = VARCHAR},'%')

mapper.update:
UPDATE td_customer_product_info c  	


		sign_status = #{signStatus, jdbcType = VARCHAR},


WHERE 
	c.id = #{id, jdbcType = VARCHAR}

	AND c.merchant_code = #{merchantCode, jdbcType = VARCHAR}


	AND c.product_code = #{productCode, jdbcType = VARCHAR}
