mapper.select:
SELECT C.*, DATE_FORMAT(C.CREATE_TIME,'%Y-%m-%d %H:%i:%s') createTime, U.EMAIL AS EMAIL, U.MOBILE AS MOBILE, U.STATE merchantState, C.STATE auditState, A.AUDIT_INFO aduitMessage, S.USER_NAME aduitUserName, F.CUST_NAME facilitatorName, M.USER_NAME custManagerName FROM TD_CUST_INFO C INNER JOIN TD_LOGIN_USER_INFO U ON C.CUST_ID = U.CUST_ID LEFT JOIN TD_AUDIT_RECODE_INFO A ON C.MERCHANT_CODE = A.MERCHANT_CODE LEFT JOIN SYS_USER S ON A.USER_ID = S.USER_ID LEFT JOIN TD_MERCHANT_APPLY_INFO T ON C.MERCHANT_CODE = T.MERCHANT_ID LEFT JOIN TD_CUST_INFO F ON T.AGENT_ID = F.MERCHANT_CODE LEFT JOIN SYS_USER M ON C.CUSTMANAGER IS NOT NULL AND C.CUSTMANAGER = M.USER_ID
AND C.MERCHANT_FLAG in ('2','0' ) AND C.STATE !="" AND (A.AUDIT_TYPE = '01' or A.AUDIT_TYPE is NULL)
AND DATE_FORMAT(C.CREATE_TIME ,'%Y-%m-%d')>= #{startCreateTime}
AND DATE_FORMAT(C.CREATE_TIME ,'%Y-%m-%d')<= #{endCreateTime}
AND C.STATE = #{auditState, jdbcType=VARCHAR}
AND U.STATE = #{merchantState, jdbcType=VARCHAR}
AND C.CUST_NAME LIKE CONCAT('%',#{custName},'%')
AND U.EMAIL LIKE CONCAT('%',#{email},'%')
AND U.MOBILE LIKE CONCAT('%',#{mobile},'%')
AND C.MERCHANT_CODE LIKE CONCAT('%',#{merchantCode},'%')
AND C.CUSTMANAGER = M.USER_ID AND M.USER_NAME LIKE CONCAT('%',#{custManager},'%')
ORDER BY C.CREATE_TIME DESC

SELECT C.*, DATE_FORMAT(C.CREATE_TIME,'%Y-%m-%d %H:%i:%s') createTime, U.EMAIL, U.MOBILE, U.STATE merchantState, C.STATE auditState, A.AUDIT_INFO aduitMessage, S.USER_NAME aduitUserName FROM TD_CUST_INFO C INNER JOIN TD_LOGIN_USER_INFO U ON C.CUST_ID = U.CUST_ID LEFT JOIN TD_AUDIT_RECODE_INFO A ON C.MERCHANT_CODE = A.MERCHANT_CODE LEFT JOIN SYS_USER S ON A.USER_ID = S.USER_ID
C.MERCHANT_FLAG = '2' AND C.STATE !=""
AND DATE_FORMAT(C.CREATE_TIME ,'%Y-%m-%d')>= #{startCreateTime}
AND DATE_FORMAT(C.CREATE_TIME ,'%Y-%m-%d')<= #{endCreateTime}
AND C.STATE = #{auditState, jdbcType=VARCHAR}
AND U.STATE = #{merchantState, jdbcType=VARCHAR}
AND C.CUST_NAME LIKE CONCAT('%',#{custName},'%')
AND U.EMAIL LIKE CONCAT('%',#{email},'%')
AND U.MOBILE LIKE CONCAT('%',#{mobile},'%')
AND C.MERCHANT_CODE LIKE CONCAT('%',#{merchantCode},'%')
ORDER BY C.CREATE_TIME DESC

mapper.update:
UPDATE 
	TD_CUST_INFO C, TD_LOGIN_USER_INFO U,TD_MERCHANT_APPLY_INFO A 
SET 
	A.APPLY_STATUS=#{applyStatus},C.STATE=#{custStatus},U.STATE=#{userStatus} 
WHERE 
	A.MERCHANT_ID=#{merchantId}
AND 
	A.MERCHANT_ID=C.MERCHANT_CODE
AND 
	C.CUST_ID=U.CUST_ID;

UPDATE 
	TD_CUST_INFO C, TD_LOGIN_USER_INFO U
SET 
	C.STATE=#{custStatus},U.STATE=#{userStatus} 
WHERE 		
	C.MERCHANT_CODE = #{merchantId}
AND 
	C.CUST_ID=U.CUST_ID;

UPDATE
	TD_LOGIN_USER_INFO U, TD_CUST_INFO C
SET
	U.LOGIN_PWD=#{loginPwd},U.ATTACH_STR=#{attachStr},C.ATTACH_STR=#{attachStr}
WHERE
	U.CUST_ID=#{custId}
	AND C.CUST_ID=#{custId}
